INSERT INTO public.summative_questions (question, question_en, options, answer, topic) VALUES
('Apa keuntungan utama dari turbin gas laut dibandingkan dengan mesin diesel?', 'What is the main advantage of a marine gas turbine compared to a diesel engine?', '{"A": {"text": "Efisiensi bahan bakar yang lebih tinggi", "text_en": "Higher fuel efficiency"}, "B": {"text": "Rasio daya terhadap berat yang sangat tinggi", "text_en": "Very high power-to-weight ratio"}, "C": {"text": "Biaya perawatan yang lebih rendah", "text_en": "Lower maintenance cost"}, "D": {"text": "Dapat menggunakan bahan bakar berkualitas rendah", "text_en": "Ability to use low-quality fuel"}}', 'B', 'Marine Engines'),
('Dalam mesin diesel 4-tak, satu siklus tenaga (power cycle) selesai dalam berapa putaran poros engkol?', 'In a 4-stroke diesel engine, one power cycle is completed in how many crankshaft revolutions?', '{"A": {"text": "Satu putaran", "text_en": "One revolution"}, "B": {"text": "Dua putaran", "text_en": "Two revolutions"}, "C": {"text": "Tiga putaran", "text_en": "Three revolutions"}, "D": {"text": "Empat putaran", "text_en": "Four revolutions"}}', 'B', 'Marine Engines'),
('Komponen apa yang mengubah gerakan linear piston menjadi gerakan rotasi?', 'Which component converts the linear motion of the piston into rotational motion?', '{"A": {"text": "Cylinder Head", "text_en": "Cylinder Head"}, "B": {"text": "Piston Rod", "text_en": "Piston Rod"}, "C": {"text": "Crankshaft", "text_en": "Crankshaft"}, "D": {"text": "Camshaft", "text_en": "Camshaft"}}', 'C', 'Marine Engines'),
('Kelemahan utama dari sistem propulsi turbin uap adalah...', 'A major disadvantage of a steam turbine propulsion system is...', '{"A": {"text": "Getaran yang tinggi", "text_en": "High vibration"}, "B": {"text": "Waktu start-up yang lama dan efisiensi rendah pada beban parsial", "text_en": "Long start-up time and low efficiency at partial loads"}, "C": {"text": "Tidak dapat menghasilkan daya tinggi", "text_en": "Inability to produce high power"}, "D": {"text": "Perawatan yang sangat sederhana", "text_en": "Very simple maintenance"}}', 'B', 'Marine Engines'),
('Mesin diesel 2-tak putaran lambat paling umum digunakan pada kapal jenis apa?', 'Slow-speed two-stroke diesel engines are most commonly used on what type of vessel?', '{"A": {"text": "Kapal pesiar cepat dan kapal perang", "text_en": "Fast yachts and naval ships"}, "B": {"text": "Kapal kontainer besar dan tanker minyak", "text_en": "Large container ships and oil tankers"}, "C": {"text": "Kapal tunda pelabuhan", "text_en": "Harbor tugboats"}, "D": {"text": "Feri penumpang kecil", "text_en": "Small passenger ferries"}}', 'B', 'Marine Engines'),
('Apa fungsi dari ''scavenge air'' pada mesin 2-tak?', 'What is the function of ''scavenge air'' in a 2-stroke engine?', '{"A": {"text": "Mendinginkan piston", "text_en": "To cool the piston"}, "B": {"text": "Mendorong gas buang keluar dari silinder dan mengisinya dengan udara segar", "text_en": "To push exhaust gases out of the cylinder and fill it with fresh air"}, "C": {"text": "Melumasi dinding silinder", "text_en": "To lubricate the cylinder walls"}, "D": {"text": "Membantu menyalakan bahan bakar", "text_en": "To help ignite the fuel"}}', 'B', 'Marine Engines'),
('Komponen apa yang membuka dan menutup katup isap dan buang pada mesin 4-tak?', 'Which component opens and closes the intake and exhaust valves in a 4-stroke engine?', '{"A": {"text": "Crankshaft", "text_en": "Crankshaft"}, "B": {"text": "Piston", "text_en": "Piston"}, "C": {"text": "Camshaft", "text_en": "Camshaft"}, "D": {"text": "Connecting Rod", "text_en": "Connecting Rod"}}', 'C', 'Marine Engines'),
('Sistem propulsi ''Combined Diesel and Gas'' (CODAG) sering digunakan untuk...', '''Combined Diesel and Gas'' (CODAG) propulsion systems are often used for...', '{"A": {"text": "Pelayaran ekonomis dengan diesel dan kecepatan tinggi dengan turbin gas", "text_en": "Economical cruising on diesel and high speed bursts on gas turbine"}, "B": {"text": "Mengurangi emisi", "text_en": "Reducing emissions"}, "C": {"text": "Menjalankan kedua mesin secara bersamaan setiap saat", "text_en": "Running both engines simultaneously at all times"}, "D": {"text": "Kapal tanker minyak", "text_en": "Oil tankers"}}', 'A', 'Marine Engines'),
('Apa fungsi dari ''piston rings''?', 'What is the function of ''piston rings''?', '{"A": {"text": "Mendinginkan piston", "text_en": "To cool the piston"}, "B": {"text": "Menghubungkan piston ke connecting rod", "text_en": "To connect the piston to the connecting rod"}, "C": {"text": "Menyekat ruang bakar dan mengontrol pelumasan", "text_en": "To seal the combustion chamber and control lubrication"}, "D": {"text": "Membuka katup", "text_en": "To open the valves"}}', 'C', 'Marine Engines'),
('Dalam siklus turbin uap, komponen apa yang mengubah uap kembali menjadi air?', 'In a steam turbine cycle, which component turns the steam back into water?', '{"A": {"text": "Boiler", "text_en": "Boiler"}, "B": {"text": "Superheater", "text_en": "Superheater"}, "C": {"text": "Turbine", "text_en": "Turbine"}, "D": {"text": "Condenser", "text_en": "Condenser"}}', 'D', 'Marine Engines'),
('Apa itu ''cylinder liner''?', 'What is a ''cylinder liner''?', '{"A": {"text": "Lapisan cat di dalam silinder", "text_en": "A layer of paint inside the cylinder"}, "B": {"text": "Selongsong yang dapat diganti tempat piston bergerak", "text_en": "A replaceable sleeve in which the piston moves"}, "C": {"text": "Pipa bahan bakar ke silinder", "text_en": "The fuel pipe to the cylinder"}, "D": {"text": "Kepala silinder", "text_en": "The cylinder head"}}', 'B', 'Marine Engines'),
('Mengapa mesin diesel putaran lambat tidak memerlukan gearbox?', 'Why do slow-speed diesel engines not require a gearbox?', '{"A": {"text": "Karena mereka tidak cukup kuat", "text_en": "Because they are not powerful enough"}, "B": {"text": "Karena kecepatan putaran optimalnya cocok dengan kecepatan baling-baling", "text_en": "Because their optimal rotational speed matches that of the propeller"}, "C": {"text": "Karena mereka berputar ke arah yang salah", "text_en": "Because they rotate in the wrong direction"}, "D": {"text": "Karena mereka menggunakan turbin gas", "text_en": "Because they use a gas turbine"}}', 'B', 'Marine Engines'),
('Apa tujuan dari ''intercooler'' pada mesin diesel dengan turbocharger?', 'What is the purpose of an ''intercooler'' on a turbocharged diesel engine?', '{"A": {"text": "Mendinginkan oli pelumas", "text_en": "To cool the lubricating oil"}, "B": {"text": "Mendinginkan udara yang dikompresi sebelum masuk ke silinder", "text_en": "To cool the compressed air before it enters the cylinder"}, "C": {"text": "Memanaskan bahan bakar", "text_en": "To heat the fuel"}, "D": {"text": "Mendinginkan gas buang", "text_en": "To cool the exhaust gas"}}', 'B', 'Marine Engines'),
('Istilah ''bore'' pada mesin mengacu pada...', 'The term ''bore'' in an engine refers to...', '{"A": {"text": "Panjang langkah piston", "text_en": "The length of the piston stroke"}, "B": {"text": "Diameter dalam silinder", "text_en": "The internal diameter of the cylinder"}, "C": {"text": "Jumlah silinder", "text_en": "The number of cylinders"}, "D": {"text": "Kecepatan mesin", "text_en": "The speed of the engine"}}', 'B', 'Marine Engines'),
('Apa keuntungan mesin 4-tak dibandingkan 2-tak?', 'What is an advantage of a 4-stroke engine over a 2-stroke?', '{"A": {"text": "Konstruksi lebih sederhana", "text_en": "Simpler construction"}, "B": {"text": "Lebih ringan untuk daya yang sama", "text_en": "Lighter for the same power"}, "C": {"text": "Konsumsi oli pelumas lebih rendah dan efisiensi bahan bakar lebih baik", "text_en": "Lower lube oil consumption and better fuel efficiency"}, "D": {"text": "Daya lebih tinggi per putaran", "text_en": "Higher power per revolution"}}', 'C', 'Marine Engines'),
('Apa fungsi dari ''flywheel''?', 'What is the function of a ''flywheel''?', '{"A": {"text": "Menyimpan energi rotasi untuk menjaga putaran tetap halus", "text_en": "To store rotational energy to keep the rotation smooth"}, "B": {"text": "Memompa air pendingin", "text_en": "To pump cooling water"}, "C": {"text": "Menyalakan mesin", "text_en": "To start the engine"}, "D": {"text": "Mengukur kecepatan mesin", "text_en": "To measure engine speed"}}', 'A', 'Marine Engines'),
('Bahan bakar apa yang paling umum digunakan oleh mesin diesel 2-tak putaran lambat?', 'What fuel is most commonly used by slow-speed 2-stroke diesel engines?', '{"A": {"text": "Marine Gas Oil (MGO)", "text_en": "Marine Gas Oil (MGO)"}, "B": {"text": "Heavy Fuel Oil (HFO)", "text_en": "Heavy Fuel Oil (HFO)"}, "C": {"text": "Liquefied Natural Gas (LNG)", "text_en": "Liquefied Natural Gas (LNG)"}, "D": {"text": "Bensin", "text_en": "Gasoline"}}', 'B', 'Marine Engines'),
('Apa itu ''supercharger''?', 'What is a ''supercharger''?', '{"A": {"text": "Kompresor udara yang digerakkan oleh gas buang", "text_en": "An air compressor driven by exhaust gas"}, "B": {"text": "Kompresor udara yang digerakkan secara mekanis oleh mesin", "text_en": "An air compressor driven mechanically by the engine"}, "C": {"text": "Sistem injeksi bahan bakar", "text_en": "A fuel injection system"}, "D": {"text": "Pompa oli pelumas", "text_en": "A lube oil pump"}}', 'B', 'Marine Engines'),
('Apa yang dimaksud dengan ''TDC''?', 'What does ''TDC'' stand for?', '{"A": {"text": "Total Diesel Consumption", "text_en": "Total Diesel Consumption"}, "B": {"text": "Top Dead Centre", "text_en": "Top Dead Centre"}, "C": {"text": "Turbine Drive Control", "text_en": "Turbine Drive Control"}, "D": {"text": "Torque Drive Camshaft", "text_en": "Torque Drive Camshaft"}}', 'B', 'Marine Engines'),
('Dalam turbin gas, komponen apa yang mengompres udara sebelum pembakaran?', 'In a gas turbine, which component compresses the air before combustion?', '{"A": {"text": "Turbine", "text_en": "Turbine"}, "B": {"text": "Combustor", "text_en": "Combustor"}, "C": {"text": "Compressor", "text_en": "Compressor"}, "D": {"text": "Nozzle", "text_en": "Nozzle"}}', 'C', 'Marine Engines'),
('Apa perbedaan mendasar antara ketel uap pipa api (fire-tube) dan pipa air (water-tube)?', 'What is the fundamental difference between a fire-tube and a water-tube boiler?', '{"A": {"text": "Pipa api untuk tekanan tinggi, pipa air untuk tekanan rendah", "text_en": "Fire-tube is for high pressure, water-tube is for low pressure"}, "B": {"text": "Pada pipa api, gas panas di dalam pipa; pada pipa air, air di dalam pipa", "text_en": "In a fire-tube, hot gas is in the tubes; in a water-tube, water is in the tubes"}, "C": {"text": "Pipa api menggunakan bahan bakar gas, pipa air menggunakan bahan bakar cair", "text_en": "Fire-tube uses gas fuel, water-tube uses liquid fuel"}, "D": {"text": "Pipa api tidak memiliki steam drum", "text_en": "Fire-tube boilers do not have a steam drum"}}', 'B', 'Marine Boiler'),
('Fungsi utama dari katup keselamatan (safety valve) pada boiler adalah...', 'The main function of a safety valve on a boiler is to...', '{"A": {"text": "Mengatur level air di dalam drum", "text_en": "Regulate the water level in the drum"}, "B": {"text": "Melepaskan tekanan uap berlebih secara otomatis", "text_en": "Automatically release excess steam pressure"}, "C": {"text": "Mengontrol aliran uap ke mesin", "text_en": "Control the steam flow to the engine"}, "D": {"text": "Membuang endapan dari dasar boiler", "text_en": "Discharge sludge from the bottom of the boiler"}}', 'B', 'Marine Boiler'),
('Mengapa ketel uap pipa air (water-tube boiler) lebih disukai untuk aplikasi tekanan tinggi?', 'Why is a water-tube boiler preferred for high-pressure applications?', '{"A": {"text": "Karena lebih murah untuk dibuat", "text_en": "Because it is cheaper to build"}, "B": {"text": "Karena perawatannya lebih mudah", "text_en": "Because its maintenance is easier"}, "C": {"text": "Karena pipa berdiameter kecil dapat menahan tekanan lebih tinggi daripada shell besar", "text_en": "Because small-diameter tubes can withstand higher pressure than a large shell"}, "D": {"text": "Karena dapat menggunakan bahan bakar apa saja", "text_en": "Because it can use any type of fuel"}}', 'C', 'Marine Boiler'),
('Prosedur ''blowdown'' pada boiler bertujuan untuk...', 'The ''blowdown'' procedure on a boiler is intended to...', '{"A": {"text": "Menaikkan tekanan uap dengan cepat", "text_en": "Quickly raise the steam pressure"}, "B": {"text": "Membuang kotoran dan endapan terlarut dari air boiler", "text_en": "Remove impurities and dissolved solids from the boiler water"}, "C": {"text": "Mendinginkan boiler setelah dimatikan", "text_en": "Cool down the boiler after shutdown"}, "D": {"text": "Menguji fungsi katup keselamatan", "text_en": "Test the function of the safety valves"}}', 'B', 'Marine Boiler'),
('Apa bahaya utama dari kondisi level air yang terlalu rendah (low water level) pada boiler yang sedang beroperasi?', 'What is the main danger of a low water level condition in an operating boiler?', '{"A": {"text": "Tekanan uap akan turun", "text_en": "The steam pressure will drop"}, "B": {"text": "Air akan terbawa ke dalam sistem uap (carryover)", "text_en": "Water will be carried over into the steam system"}, "C": {"text": "Pemanasan berlebih (overheating) pada pelat atau pipa, yang dapat menyebabkan ledakan", "text_en": "Overheating of plates or tubes, which can lead to an explosion"}, "D": {"text": "Efisiensi bahan bakar akan menurun", "text_en": "Fuel efficiency will decrease"}}', 'C', 'Marine Boiler'),
('Apa fungsi dari ''steam drum'' pada water-tube boiler?', 'What is the function of a ''steam drum'' in a water-tube boiler?', '{"A": {"text": "Menyimpan air umpan", "text_en": "To store feedwater"}, "B": {"text": "Memisahkan uap dari air", "text_en": "To separate steam from water"}, "C": {"text": "Tempat pembakaran terjadi", "text_en": "Where combustion occurs"}, "D": {"text": "Mendinginkan uap", "text_en": "To cool the steam"}}', 'B', 'Marine Boiler'),
('Perlengkapan apa yang digunakan untuk memantau level air di dalam boiler secara visual?', 'Which fitting is used to visually monitor the water level in a boiler?', '{"A": {"text": "Pressure Gauge", "text_en": "Pressure Gauge"}, "B": {"text": "Safety Valve", "text_en": "Safety Valve"}, "C": {"text": "Gauge Glass", "text_en": "Gauge Glass"}, "D": {"text": "Feed Check Valve", "text_en": "Feed Check Valve"}}', 'C', 'Marine Boiler'),
('Apa itu ''soot blowing''?', 'What is ''soot blowing''?', '{"A": {"text": "Proses membersihkan jelaga dari permukaan pipa untuk meningkatkan perpindahan panas", "text_en": "The process of cleaning soot from tube surfaces to improve heat transfer"}, "B": {"text": "Proses membuang air dari boiler", "text_en": "The process of draining water from the boiler"}, "C": {"text": "Proses menyalakan burner", "text_en": "The process of lighting the burner"}, "D": {"text": "Proses menguji katup keselamatan", "text_en": "The process of testing the safety valves"}}', 'A', 'Marine Boiler'),
('Apa bahaya dari ''carryover'' uap?', 'What is the danger of steam ''carryover''?', '{"A": {"text": "Menyebabkan boiler kehilangan tekanan", "text_en": "It causes the boiler to lose pressure"}, "B": {"text": "Dapat merusak bilah turbin dan perpipaan", "text_en": "It can damage turbine blades and piping"}, "C": {"text": "Menyebabkan overheating pada boiler", "text_en": "It causes the boiler to overheat"}, "D": {"text": "Meningkatkan konsumsi bahan bakar", "text_en": "It increases fuel consumption"}}', 'B', 'Marine Boiler'),
('Apa fungsi dari ''feed check valve''?', 'What is the function of a ''feed check valve''?', '{"A": {"text": "Mengontrol aliran uap keluar dari boiler", "text_en": "To control the steam flow out of the boiler"}, "B": {"text": "Mengizinkan air masuk ke boiler dan mencegahnya keluar kembali", "text_en": "To allow water into the boiler and prevent it from flowing back out"}, "C": {"text": "Melepaskan tekanan berlebih", "text_en": "To release excess pressure"}, "D": {"text": "Menunjukkan level air", "text_en": "To indicate the water level"}}', 'B', 'Marine Boiler'),
('Di mana ''furnace'' berada pada water-tube boiler?', 'Where is the ''furnace'' located in a water-tube boiler?', '{"A": {"text": "Di dalam steam drum", "text_en": "Inside the steam drum"}, "B": {"text": "Di sekitar pipa-pipa yang berisi air", "text_en": "Surrounding the tubes that contain water"}, "C": {"text": "Di dalam pipa-pipa", "text_en": "Inside the tubes"}, "D": {"text": "Di atas boiler", "text_en": "On top of the boiler"}}', 'B', 'Marine Boiler'),
('Apa tujuan dari ''refractory'' di dalam furnace?', 'What is the purpose of ''refractory'' inside a furnace?', '{"A": {"text": "Meningkatkan perpindahan panas", "text_en": "To increase heat transfer"}, "B": {"text": "Melindungi casing boiler dari panas tinggi dan membantu pembakaran", "text_en": "To protect the boiler casing from high heat and aid combustion"}, "C": {"text": "Membersihkan jelaga", "text_en": "To clean soot"}, "D": {"text": "Mendinginkan furnace", "text_en": "To cool the furnace"}}', 'B', 'Marine Boiler'),
('Apa yang dimaksud dengan ''TDS'' dalam konteks air boiler?', 'What does ''TDS'' mean in the context of boiler water?', '{"A": {"text": "Total Dissolved Solids", "text_en": "Total Dissolved Solids"}, "B": {"text": "Total Drum Steam", "text_en": "Total Drum Steam"}, "C": {"text": "Temperature Differential System", "text_en": "Temperature Differential System"}, "D": {"text": "Tube Damage Sensor", "text_en": "Tube Damage Sensor"}}', 'A', 'Marine Boiler'),
('Mengapa sirkulasi air yang baik penting di dalam boiler?', 'Why is good water circulation important inside a boiler?', '{"A": {"text": "Untuk menjaga air tetap bersih", "text_en": "To keep the water clean"}, "B": {"text": "Untuk mencegah overheating pada permukaan pemanas", "text_en": "To prevent overheating of the heating surfaces"}, "C": {"text": "Untuk mengurangi tekanan", "text_en": "To reduce pressure"}, "D": {"text": "Untuk menghasilkan uap lebih cepat", "text_en": "To generate steam faster"}}', 'B', 'Marine Boiler'),
('Apa fungsi dari ''superheater''?', 'What is the function of a ''superheater''?', '{"A": {"text": "Memanaskan air umpan", "text_en": "To heat the feedwater"}, "B": {"text": "Memanaskan uap jenuh (saturated steam) di atas suhu didihnya", "text_en": "To heat saturated steam above its boiling temperature"}, "C": {"text": "Mendinginkan gas buang", "text_en": "To cool the exhaust gas"}, "D": {"text": "Mengontrol pembakaran", "text_en": "To control combustion"}}', 'B', 'Marine Boiler'),
('Apa tindakan pertama yang harus diambil jika level air tidak terlihat di gauge glass?', 'What is the first action to take if the water level is not visible in the gauge glass?', '{"A": {"text": "Menambahkan lebih banyak air", "text_en": "Add more water"}, "B": {"text": "Mematikan burner", "text_en": "Shut off the burner"}, "C": {"text": "Menaikkan tekanan", "text_en": "Increase the pressure"}, "D": {"text": "Melakukan blowdown", "text_en": "Perform a blowdown"}}', 'B', 'Marine Boiler'),
('Boiler jenis apa yang lebih umum digunakan sebagai ''auxiliary boiler'' di kapal motor?', 'Which type of boiler is more common as an ''auxiliary boiler'' on a motor vessel?', '{"A": {"text": "Water-tube boiler", "text_en": "Water-tube boiler"}, "B": {"text": "Fire-tube boiler", "text_en": "Fire-tube boiler"}, "C": {"text": "Nuclear boiler", "text_en": "Nuclear boiler"}, "D": {"text": "Gas turbine boiler", "text_en": "Gas turbine boiler"}}', 'B', 'Marine Boiler'),
('Apa itu ''economizer''?', 'What is an ''economizer''?', '{"A": {"text": "Perangkat untuk menghemat bahan bakar dengan memanaskan air umpan menggunakan gas buang", "text_en": "A device to save fuel by heating feedwater using exhaust gases"}, "B": {"text": "Perangkat untuk menghemat air", "text_en": "A device to save water"}, "C": {"text": "Jenis burner yang efisien", "text_en": "An efficient type of burner"}, "D": {"text": "Sistem kontrol otomatis", "text_en": "An automatic control system"}}', 'A', 'Marine Boiler'),
('Apa yang menyebabkan pembentukan kerak (scale) di dalam boiler?', 'What causes scale formation inside a boiler?', '{"A": {"text": "Terlalu banyak bahan bakar", "text_en": "Too much fuel"}, "B": {"text": "Tekanan terlalu tinggi", "text_en": "Pressure is too high"}, "C": {"text": "Endapan mineral dari air umpan yang berkualitas buruk", "text_en": "Mineral deposits from poor quality feedwater"}, "D": {"text": "Terlalu banyak blowdown", "text_en": "Too much blowdown"}}', 'C', 'Marine Boiler'),
('Apa fungsi dari ''burner''?', 'What is the function of a ''burner''?', '{"A": {"text": "Menyemprotkan bahan bakar dalam bentuk kabut halus untuk pembakaran yang efisien", "text_en": "To spray fuel in a fine mist for efficient combustion"}, "B": {"text": "Mengontrol level air", "text_en": "To control the water level"}, "C": {"text": "Membersihkan pipa", "text_en": "To clean the tubes"}, "D": {"text": "Mengukur suhu", "text_en": "To measure temperature"}}', 'A', 'Marine Boiler'),
('Komponen yang mentransfer daya dorong (thrust) dari poros baling-baling ke badan kapal adalah...', 'The component that transfers the thrust from the propeller shaft to the ship''s hull is the...', '{"A": {"text": "Plummer block", "text_en": "Plummer block"}, "B": {"text": "Stern tube", "text_en": "Stern tube"}, "C": {"text": "Thrust block", "text_en": "Thrust block"}, "D": {"text": "Intermediate shaft", "text_en": "Intermediate shaft"}}', 'C', 'Shafting & Propeller'),
('Keuntungan utama dari Controllable Pitch Propeller (CPP) adalah...', 'The main advantage of a Controllable Pitch Propeller (CPP) is...', '{"A": {"text": "Lebih kuat dan lebih murah daripada FPP", "text_en": "It is stronger and cheaper than an FPP"}, "B": {"text": "Memungkinkan perubahan kecepatan dan arah tanpa mengubah putaran mesin", "text_en": "It allows for speed and direction changes without changing engine rotation"}, "C": {"text": "Tidak memerlukan tabung buritan (stern tube)", "text_en": "It does not require a stern tube"}, "D": {"text": "Lebih efisien pada kecepatan jelajah konstan", "text_en": "It is more efficient at a constant cruising speed"}}', 'B', 'Shafting & Propeller'),
('Fungsi utama dari tabung buritan (stern tube) adalah...', 'The main function of the stern tube is to...', '{"A": {"text": "Menghasilkan daya dorong tambahan", "text_en": "Generate additional thrust"}, "B": {"text": "Menopang poros baling-baling dan mencegah air laut masuk", "text_en": "Support the propeller shaft and prevent seawater entry"}, "C": {"text": "Mendinginkan poros baling-baling", "text_en": "Cool the propeller shaft"}, "D": {"text": "Menghubungkan poros ke gearbox", "text_en": "Connect the shaft to the gearbox"}}', 'B', 'Shafting & Propeller'),
('Getaran berlebih di area buritan kapal seringkali merupakan gejala dari...', 'Excessive vibration in the aft area of a ship is often a symptom of...', '{"A": {"text": "Bahan bakar berkualitas buruk", "text_en": "Poor quality fuel"}, "B": {"text": "Kelurusan poros yang buruk (poor alignment) atau baling-baling rusak", "text_en": "Poor shaft alignment or a damaged propeller"}, "C": {"text": "Tekanan oli pelumas yang rendah", "text_en": "Low lube oil pressure"}, "D": {"text": "Sistem pendingin yang tidak efisien", "text_en": "An inefficient cooling system"}}', 'B', 'Shafting & Propeller'),
('Bantalan yang digunakan untuk menopang poros antara (intermediate shaft) di sepanjang terowongan poros disebut...', 'The bearings used to support the intermediate shaft along the shaft tunnel are called...', '{"A": {"text": "Thrust bearings", "text_en": "Thrust bearings"}, "B": {"text": "Stern tube bearings", "text_en": "Stern tube bearings"}, "C": {"text": "Main bearings", "text_en": "Main bearings"}, "D": {"text": "Plummer blocks", "text_en": "Plummer blocks"}}', 'D', 'Shafting & Propeller'),
('Apa itu ''propeller slip''?', 'What is ''propeller slip''?', '{"A": {"text": "Ketika baling-baling terlepas dari poros", "text_en": "When the propeller detaches from the shaft"}, "B": {"text": "Perbedaan antara kecepatan teoretis dan kecepatan aktual kapal", "text_en": "The difference between the theoretical and actual speed of the ship"}, "C": {"text": "Ketika baling-baling berputar mundur", "text_en": "When the propeller spins backwards"}, "D": {"text": "Jenis pelumasan untuk baling-baling", "text_en": "A type of lubrication for the propeller"}}', 'B', 'Shafting & Propeller'),
('Poros terakhir dalam sistem, yang menembus lambung kapal, disebut...', 'The final shaft in the system, which penetrates the hull, is called the...', '{"A": {"text": "Intermediate shaft", "text_en": "Intermediate shaft"}, "B": {"text": "Thrust shaft", "text_en": "Thrust shaft"}, "C": {"text": "Tail shaft / Propeller shaft", "text_en": "Tail shaft / Propeller shaft"}, "D": {"text": "Crankshaft", "text_en": "Crankshaft"}}', 'C', 'Shafting & Propeller'),
('Apa fungsi dari ''stern tube seals''?', 'What is the function of ''stern tube seals''?', '{"A": {"text": "Mengurangi getaran", "text_en": "To reduce vibration"}, "B": {"text": "Mencegah air laut masuk ke kamar mesin dan oli keluar", "text_en": "To prevent seawater from entering the engine room and oil from escaping"}, "C": {"text": "Menopang berat poros", "text_en": "To support the weight of the shaft"}, "D": {"text": "Mendinginkan poros", "text_en": "To cool the shaft"}}', 'B', 'Shafting & Propeller'),
('Apa kelemahan dari CPP dibandingkan FPP?', 'What is a disadvantage of a CPP compared to an FPP?', '{"A": {"text": "Kurang efisien", "text_en": "Less efficient"}, "B": {"text": "Lebih kompleks, mahal, dan memerlukan perawatan lebih", "text_en": "More complex, expensive, and requires more maintenance"}, "C": {"text": "Tidak dapat digunakan pada kapal besar", "text_en": "Cannot be used on large ships"}, "D": {"text": "Menghasilkan lebih banyak getaran", "text_en": "Produces more vibration"}}', 'B', 'Shafting & Propeller'),
('Apa itu ''cavitation'' pada baling-baling?', 'What is ''cavitation'' on a propeller?', '{"A": {"text": "Pertumbuhan biota laut pada baling-baling", "text_en": "Marine growth on the propeller"}, "B": {"text": "Pembentukan dan pecahnya gelembung uap karena tekanan rendah, yang dapat merusak bilah", "text_en": "The formation and collapse of vapor bubbles due to low pressure, which can damage the blades"}, "C": {"text": "Proses pemasangan baling-baling", "text_en": "The process of installing a propeller"}, "D": {"text": "Jenis korosi", "text_en": "A type of corrosion"}}', 'B', 'Shafting & Propeller'),
('Bagaimana cara memasang baling-baling ke poros ekor?', 'How is a propeller fitted to the tail shaft?', '{"A": {"text": "Dengan dilas", "text_en": "By welding"}, "B": {"text": "Dengan dilem", "text_en": "By gluing"}, "C": {"text": "Dengan pemasangan tirus (taper fit) dan mur besar", "text_en": "With a taper fit and a large nut"}, "D": {"text": "Dengan baut", "text_en": "With bolts"}}', 'C', 'Shafting & Propeller'),
('Apa tujuan dari ''rope guard''?', 'What is the purpose of a ''rope guard''?', '{"A": {"text": "Melindungi tali tambat agar tidak putus", "text_en": "To protect mooring ropes from breaking"}, "B": {"text": "Mencegah tali atau jaring melilit di sekitar poros dan perapat", "text_en": "To prevent ropes or nets from wrapping around the shaft and seals"}, "C": {"text": "Menyimpan tali", "text_en": "To store ropes"}, "D": {"text": "Mengukur ketegangan tali", "text_en": "To measure rope tension"}}', 'B', 'Shafting & Propeller'),
('Apa yang dimaksud dengan ''pitch'' baling-baling?', 'What does the ''pitch'' of a propeller refer to?', '{"A": {"text": "Diameter baling-baling", "text_en": "The diameter of the propeller"}, "B": {"text": "Jumlah bilah", "text_en": "The number of blades"}, "C": {"text": "Jarak maju teoretis per satu putaran", "text_en": "The theoretical forward distance per one revolution"}, "D": {"text": "Berat baling-baling", "text_en": "The weight of the propeller"}}', 'C', 'Shafting & Propeller'),
('Pemeriksaan apa yang dilakukan untuk memastikan kelurusan poros?', 'What check is performed to ensure shaft alignment?', '{"A": {"text": "Pemeriksaan suhu", "text_en": "Temperature check"}, "B": {"text": "Pemeriksaan getaran", "text_en": "Vibration check"}, "C": {"text": "Pengukuran ''poker gauge'' atau ''deflection gauge''", "text_en": "Poker gauge or deflection gauge measurement"}, "D": {"text": "Pemeriksaan visual", "text_en": "Visual inspection"}}', 'C', 'Shafting & Propeller'),
('Apa fungsi dari ''fairwater cone'' atau ''propeller boss cap''?', 'What is the function of a ''fairwater cone'' or ''propeller boss cap''?', '{"A": {"text": "Mengurangi hambatan dan turbulensi di belakang baling-baling", "text_en": "To reduce drag and turbulence behind the propeller"}, "B": {"text": "Menahan baling-baling di tempatnya", "text_en": "To hold the propeller in place"}, "C": {"text": "Melumasi baling-baling", "text_en": "To lubricate the propeller"}, "D": {"text": "Mendinginkan baling-baling", "text_en": "To cool the propeller"}}', 'A', 'Shafting & Propeller'),
('Bahan apa yang paling umum digunakan untuk membuat baling-baling kapal besar?', 'What is the most common material for large ship propellers?', '{"A": {"text": "Baja tahan karat (Stainless Steel)", "text_en": "Stainless Steel"}, "B": {"text": "Paduan nikel-aluminium-perunggu (Nickel-Aluminium-Bronze alloy)", "text_en": "Nickel-Aluminium-Bronze alloy"}, "C": {"text": "Titanium", "text_en": "Titanium"}, "D": {"text": "Besi cor (Cast Iron)", "text_en": "Cast Iron"}}', 'B', 'Shafting & Propeller'),
('Apa itu ''shaft generator''?', 'What is a ''shaft generator''?', '{"A": {"text": "Generator yang digerakkan oleh poros utama untuk menghasilkan listrik saat berlayar", "text_en": "A generator driven by the main shaft to produce electricity while sailing"}, "B": {"text": "Generator kecil untuk poros", "text_en": "A small generator for the shaft"}, "C": {"text": "Motor untuk memutar poros", "text_en": "A motor to turn the shaft"}, "D": {"text": "Alat ukur pada poros", "text_en": "A measuring device on the shaft"}}', 'A', 'Shafting & Propeller'),
('Apa yang menopang berat poros di dalam stern tube?', 'What supports the weight of the shaft inside the stern tube?', '{"A": {"text": "Air laut", "text_en": "Sea water"}, "B": {"text": "Perapat (seals)", "text_en": "The seals"}, "C": {"text": "Bantalan (bearings)", "text_en": "The bearings"}, "D": {"text": "Tekanan oli", "text_en": "Oil pressure"}}', 'C', 'Shafting & Propeller'),
('Untuk kapal yang membutuhkan kemampuan manuver sangat tinggi, jenis baling-baling apa yang lebih disukai?', 'For a ship requiring very high maneuverability, which propeller type is preferred?', '{"A": {"text": "Fixed Pitch Propeller (FPP)", "text_en": "Fixed Pitch Propeller (FPP)"}, "B": {"text": "Controllable Pitch Propeller (CPP)", "text_en": "Controllable Pitch Propeller (CPP)"}, "C": {"text": "Keduanya sama baiknya", "text_en": "Both are equally good"}, "D": {"text": "Tidak ada baling-baling yang digunakan", "text_en": "No propeller is used"}}', 'B', 'Shafting & Propeller'),
('Apa yang menghubungkan bagian-bagian dari poros antara (intermediate shafts)?', 'What connects the sections of the intermediate shafts together?', '{"A": {"text": "Las", "text_en": "Welds"}, "B": {"text": "Kopling flensa baut (bolted flange couplings)", "text_en": "Bolted flange couplings"}, "C": {"text": "Lem", "text_en": "Glue"}, "D": {"text": "Rantai", "text_en": "Chains"}}', 'B', 'Shafting & Propeller'),
('Tujuan utama dari pemisah sentrifugal (centrifugal separator/purifier) untuk bahan bakar adalah...', 'The main purpose of a centrifugal separator/purifier for fuel oil is to...', '{"A": {"text": "Menaikkan suhu bahan bakar", "text_en": "Increase the fuel''s temperature"}, "B": {"text": "Menurunkan viskositas bahan bakar", "text_en": "Decrease the fuel''s viscosity"}, "C": {"text": "Memisahkan air dan lumpur (sludge) dari bahan bakar", "text_en": "Separate water and sludge from the fuel"}, "D": {"text": "Mengukur laju aliran bahan bakar", "text_en": "Measure the fuel flow rate"}}', 'C', 'Other Auxiliaries'),
('Bagaimana Fresh Water Generator (FWG) tipe distilasi vakum menghasilkan air tawar?', 'How does a vacuum distillation type Fresh Water Generator (FWG) produce fresh water?', '{"A": {"text": "Dengan menyaring garam menggunakan filter membran", "text_en": "By filtering salt using membrane filters"}, "B": {"text": "Dengan merebus air laut pada tekanan atmosfer", "text_en": "By boiling sea water at atmospheric pressure"}, "C": {"text": "Dengan merebus air laut pada suhu rendah dalam kondisi vakum", "text_en": "By boiling sea water at a low temperature under vacuum"}, "D": {"text": "Dengan menambahkan bahan kimia untuk memisahkan garam", "text_en": "By adding chemicals to separate the salt"}}', 'C', 'Other Auxiliaries'),
('Manakah dari berikut ini yang BUKAN merupakan syarat untuk sinkronisasi generator?', 'Which of the following is NOT a condition for synchronizing a generator?', '{"A": {"text": "Tegangan Sama (Same Voltage)", "text_en": "Same Voltage"}, "B": {"text": "Frekuensi Sama (Same Frequency)", "text_en": "Same Frequency"}, "C": {"text": "Beban Sama (Same Load)", "text_en": "Same Load"}, "D": {"text": "Sudut Fasa Sama (Same Phase Angle)", "text_en": "Same Phase Angle"}}', 'C', 'Other Auxiliaries'),
('Pompa jenis apa yang paling cocok untuk memindahkan cairan kental (viscous) seperti oli pelumas pada tekanan tinggi?', 'Which type of pump is most suitable for moving viscous fluids like lube oil at high pressure?', '{"A": {"text": "Pompa Sentrifugal (Centrifugal Pump)", "text_en": "Centrifugal Pump"}, "B": {"text": "Pompa Roda Gigi (Gear Pump)", "text_en": "Gear Pump"}, "C": {"text": "Pompa Ejektor (Ejector Pump)", "text_en": "Ejector Pump"}, "D": {"text": "Pompa Propeller (Propeller Pump)", "text_en": "Propeller Pump"}}', 'B', 'Other Auxiliaries'),
('Dalam siklus refrigerasi, komponen apa yang menyerap panas dari ruangan yang didinginkan?', 'In a refrigeration cycle, which component absorbs heat from the space being cooled?', '{"A": {"text": "Kompresor (Compressor)", "text_en": "Compressor"}, "B": {"text": "Kondensor (Condenser)", "text_en": "Condenser"}, "C": {"text": "Katup Ekspansi (Expansion Valve)", "text_en": "Expansion Valve"}, "D": {"text": "Evaporator", "text_en": "Evaporator"}}', 'D', 'Other Auxiliaries'),
('Apa fungsi utama dari kompresor udara di kapal?', 'What is the main function of an air compressor on a ship?', '{"A": {"text": "Menyediakan udara untuk pernapasan", "text_en": "To provide breathing air"}, "B": {"text": "Menyediakan udara bertekanan untuk menyalakan mesin dan sistem kontrol", "text_en": "To provide compressed air for engine starting and control systems"}, "C": {"text": "Mendinginkan kamar mesin", "text_en": "To cool the engine room"}, "D": {"text": "Memompa air", "text_en": "To pump water"}}', 'B', 'Other Auxiliaries'),
('Apa perbedaan antara pompa sentrifugal dan pompa perpindahan positif?', 'What is the difference between a centrifugal pump and a positive displacement pump?', '{"A": {"text": "Pompa sentrifugal untuk tekanan tinggi, pompa perpindahan positif untuk aliran tinggi", "text_en": "Centrifugal is for high pressure, positive displacement is for high flow"}, "B": {"text": "Pompa sentrifugal untuk aliran tinggi, pompa perpindahan positif untuk tekanan tinggi", "text_en": "Centrifugal is for high flow, positive displacement is for high pressure"}, "C": {"text": "Tidak ada perbedaan", "text_en": "There is no difference"}, "D": {"text": "Pompa sentrifugal menggunakan piston", "text_en": "Centrifugal pumps use pistons"}}', 'B', 'Other Auxiliaries'),
('Dalam sistem refrigerasi, di mana refrigeran berubah dari gas menjadi cair?', 'In a refrigeration system, where does the refrigerant change from a gas to a liquid?', '{"A": {"text": "Evaporator", "text_en": "Evaporator"}, "B": {"text": "Compressor", "text_en": "Compressor"}, "C": {"text": "Condenser", "text_en": "Condenser"}, "D": {"text": "Expansion Valve", "text_en": "Expansion Valve"}}', 'C', 'Other Auxiliaries'),
('Apa itu ''oily water separator'' (OWS)?', 'What is an ''oily water separator'' (OWS)?', '{"A": {"text": "Peralatan untuk memisahkan oli dari air got (bilge water) sebelum dibuang ke laut", "text_en": "Equipment to separate oil from bilge water before discharging overboard"}, "B": {"text": "Peralatan untuk mencampur oli dan air", "text_en": "Equipment to mix oil and water"}, "C": {"text": "Peralatan untuk memurnikan oli pelumas", "text_en": "Equipment to purify lube oil"}, "D": {"text": "Peralatan untuk memanaskan air", "text_en": "Equipment to heat water"}}', 'A', 'Other Auxiliaries'),
('Apa fungsi dari ''gravity disc'' pada purifier?', 'What is the function of a ''gravity disc'' in a purifier?', '{"A": {"text": "Mengatur kecepatan putaran", "text_en": "To regulate the rotational speed"}, "B": {"text": "Menentukan antarmuka (interface) antara oli dan air untuk pemisahan yang efektif", "text_en": "To establish the interface between oil and water for effective separation"}, "C": {"text": "Membuang lumpur", "text_en": "To discharge sludge"}, "D": {"text": "Mendinginkan oli", "text_en": "To cool the oil"}}', 'B', 'Other Auxiliaries'),
('Apa sumber panas yang paling umum digunakan untuk FWG tipe distilasi?', 'What is the most common heat source for a distillation type FWG?', '{"A": {"text": "Burner khusus", "text_en": "A dedicated burner"}, "B": {"text": "Panas dari gas buang boiler", "text_en": "Heat from boiler exhaust gas"}, "C": {"text": "Panas dari air pendingin jaket mesin utama", "text_en": "Heat from the main engine jacket cooling water"}, "D": {"text": "Listrik", "text_en": "Electricity"}}', 'C', 'Other Auxiliaries'),
('Apa fungsi dari ''steering gear''?', 'What is the function of the ''steering gear''?', '{"A": {"text": "Menggerakkan kapal maju", "text_en": "To move the ship forward"}, "B": {"text": "Menghasilkan listrik", "text_en": "To generate electricity"}, "C": {"text": "Mesin hidrolik atau elektrik yang menggerakkan daun kemudi", "text_en": "The hydraulic or electric machinery that moves the rudder"}, "D": {"text": "Mengontrol mesin utama", "text_en": "To control the main engine"}}', 'C', 'Other Auxiliaries'),
('Apa itu ''incinerator''?', 'What is an ''incinerator''?', '{"A": {"text": "Tungku untuk membakar sampah padat dan lumpur oli", "text_en": "A furnace for burning solid waste and oil sludge"}, "B": {"text": "Jenis pompa", "text_en": "A type of pump"}, "C": {"text": "Sistem pendingin", "text_en": "A cooling system"}, "D": {"text": "Generator darurat", "text_en": "An emergency generator"}}', 'A', 'Other Auxiliaries'),
('Mengapa udara kontrol harus sangat kering?', 'Why must control air be very dry?', '{"A": {"text": "Agar lebih ringan", "text_en": "To make it lighter"}, "B": {"text": "Untuk mencegah korosi dan pembekuan pada komponen kontrol pneumatik yang sensitif", "text_en": "To prevent corrosion and freezing in sensitive pneumatic control components"}, "C": {"text": "Agar lebih mudah dikompresi", "text_en": "To make it easier to compress"}, "D": {"text": "Untuk mengurangi kebisingan", "text_en": "To reduce noise"}}', 'B', 'Other Auxiliaries'),
('Apa fungsi dari ''plate cooler''?', 'What is the function of a ''plate cooler''?', '{"A": {"text": "Mendinginkan piring di dapur", "text_en": "To cool plates in the galley"}, "B": {"text": "Jenis penukar panas yang menggunakan pelat untuk mentransfer panas", "text_en": "A type of heat exchanger that uses plates to transfer heat"}, "C": {"text": "Melindungi lambung kapal", "text_en": "To protect the ship''s hull"}, "D": {"text": "Memanaskan ruangan", "text_en": "To heat a room"}}', 'B', 'Other Auxiliaries'),
('Apa yang terjadi pada refrigeran saat melewati ''expansion valve''?', 'What happens to the refrigerant as it passes through the ''expansion valve''?', '{"A": {"text": "Tekanan dan suhunya naik", "text_en": "Its pressure and temperature increase"}, "B": {"text": "Tekanan dan suhunya turun drastis", "text_en": "Its pressure and temperature drop drastically"}, "C": {"text": "Itu menjadi gas super panas", "text_en": "It becomes a superheated gas"}, "D": {"text": "Itu disaring", "text_en": "It gets filtered"}}', 'B', 'Other Auxiliaries'),
('Apa itu ''sewage treatment plant''?', 'What is a ''sewage treatment plant''?', '{"A": {"text": "Pabrik pengolahan air minum", "text_en": "A drinking water treatment plant"}, "B": {"text": "Sistem untuk mengolah air limbah dari toilet sebelum dibuang", "text_en": "A system to treat wastewater from toilets before discharge"}, "C": {"text": "Sistem untuk membersihkan air got", "text_en": "A system for cleaning bilge water"}, "D": {"text": "Sistem pendingin", "text_en": "A cooling system"}}', 'B', 'Other Auxiliaries'),
('Apa penggerak utama (prime mover) yang paling umum untuk generator di kapal?', 'What is the most common prime mover for a generator on a ship?', '{"A": {"text": "Turbin uap", "text_en": "A steam turbine"}, "B": {"text": "Mesin diesel 4-tak", "text_en": "A 4-stroke diesel engine"}, "C": {"text": "Turbin gas", "text_en": "A gas turbine"}, "D": {"text": "Motor listrik", "text_en": "An electric motor"}}', 'B', 'Other Auxiliaries'),
('Apa fungsi dari ''governor'' pada mesin generator?', 'What is the function of a ''governor'' on a generator engine?', '{"A": {"text": "Mengatur tegangan", "text_en": "To regulate the voltage"}, "B": {"text": "Menjaga kecepatan mesin (dan frekuensi) tetap konstan meskipun beban berubah", "text_en": "To keep the engine speed (and thus frequency) constant despite load changes"}, "C": {"text": "Menghentikan mesin dalam keadaan darurat", "text_en": "To stop the engine in an emergency"}, "D": {"text": "Melumasi mesin", "text_en": "To lubricate the engine"}}', 'B', 'Other Auxiliaries'),
('Apa itu ''bow thruster''?', 'What is a ''bow thruster''?', '{"A": {"text": "Baling-baling tambahan di haluan untuk membantu manuver", "text_en": "An extra propeller at the bow to assist with maneuvering"}, "B": {"text": "Jangkar darurat", "text_en": "An emergency anchor"}, "C": {"text": "Sistem pemadam kebakaran", "text_en": "A fire-fighting system"}, "D": {"text": "Lampu navigasi di haluan", "text_en": "A navigation light at the bow"}}', 'A', 'Other Auxiliaries'),
('Apa keuntungan utama sistem bahan bakar ''common rail'' dibandingkan sistem pompa ''jerk'' tradisional?', 'What is a key advantage of a ''common rail'' fuel system over a traditional ''jerk'' pump system?', '{"A": {"text": "Kontrol waktu dan tekanan injeksi yang lebih presisi", "text_en": "More precise control over injection timing and pressure"}, "B": {"text": "Lebih sederhana dan lebih sedikit komponen", "text_en": "Simpler and has fewer components"}, "C": {"text": "Tidak memerlukan bahan bakar bertekanan tinggi", "text_en": "Does not require high-pressure fuel"}, "D": {"text": "Hanya digunakan untuk mesin kecil", "text_en": "It is only used for small engines"}}', 'A', 'Marine Engines'),
('Dalam sistem pendingin mesin laut yang umum, fluida apa yang secara langsung mendinginkan silinder mesin?', 'In a typical marine engine cooling system, what fluid directly cools the engine cylinders?', '{"A": {"text": "Air laut", "text_en": "Sea water"}, "B": {"text": "Air tawar (Freshwater)", "text_en": "Freshwater"}, "C": {"text": "Oli pelumas", "text_en": "Lubricating oil"}, "D": {"text": "Udara", "text_en": "Air"}}', 'B', 'Marine Engines'),
('Tujuan utama sistem oli pelumas pada mesin adalah...', 'The primary purpose of the lubricating oil system in an engine is...', '{"A": {"text": "Mendinginkan bahan bakar", "text_en": "To cool the fuel"}, "B": {"text": "Mengurangi gesekan dan keausan di antara bagian yang bergerak", "text_en": "To reduce friction and wear between moving parts"}, "C": {"text": "Meningkatkan tenaga mesin", "text_en": "To increase engine power"}, "D": {"text": "Membersihkan gas buang", "text_en": "To clean the exhaust gas"}}', 'B', 'Marine Engines'),
('''Exhaust gas boiler'' atau ''economizer'' digunakan untuk...', 'An ''exhaust gas boiler'' or ''economizer'' is used to...', '{"A": {"text": "Menghasilkan uap menggunakan panas dari gas buang mesin utama", "text_en": "Produce steam using heat from the main engine exhaust"}, "B": {"text": "Meredam suara gas buang", "text_en": "Muffle the sound of the exhaust gas"}, "C": {"text": "Mendinginkan gas buang sebelum dilepaskan", "text_en": "Cool the exhaust gas before release"}, "D": {"text": "Meningkatkan tekanan gas buang", "text_en": "Increase the exhaust gas pressure"}}', 'A', 'Marine Engines'),
('Apa perbedaan antara ''indicated power'' dan ''brake power''?', 'What is the difference between ''indicated power'' and ''brake power''?', '{"A": {"text": "Tidak ada perbedaan", "text_en": "There is no difference"}, "B": {"text": "Indicated power adalah daya di baling-baling, brake power adalah daya di mesin", "text_en": "Indicated power is power at the propeller, brake power is power at the engine"}, "C": {"text": "Indicated power adalah daya teoretis di dalam silinder, brake power adalah daya yang dapat digunakan di poros engkol", "text_en": "Indicated power is the theoretical power inside the cylinders, brake power is the usable power at the crankshaft"}, "D": {"text": "Brake power selalu lebih besar dari indicated power", "text_en": "Brake power is always greater than indicated power"}}', 'C', 'Marine Engines'),
('Apa perbedaan antara ''scum blowdown'' dan ''bottom blowdown''?', 'What is the difference between a ''scum blowdown'' and a ''bottom blowdown''?', '{"A": {"text": "Keduanya sama, hanya beda nama", "text_en": "They are the same, just different names"}, "B": {"text": "Scum blowdown untuk tekanan tinggi, bottom blowdown untuk tekanan rendah", "text_en": "Scum blowdown is for high pressure, bottom blowdown is for low pressure"}, "C": {"text": "Scum membuang kotoran di permukaan air, bottom membuang endapan di dasar", "text_en": "Scum removes impurities at the water surface, bottom removes sludge at the base"}, "D": {"text": "Scum blowdown menggunakan uap, bottom blowdown menggunakan air", "text_en": "Scum blowdown uses steam, bottom blowdown uses water"}}', 'C', 'Marine Boiler'),
('''Composite boiler'' adalah jenis boiler yang dapat...', 'A ''composite boiler'' is a type of boiler that can...', '{"A": {"text": "Hanya menggunakan bahan bakar padat", "text_en": "Only use solid fuel"}, "B": {"text": "Dinyalakan dengan oli dan/atau menggunakan gas buang dari mesin", "text_en": "Be oil-fired and/or use exhaust gas from an engine"}, "C": {"text": "Menghasilkan uap dan air panas secara bersamaan", "text_en": "Produce steam and hot water simultaneously"}, "D": {"text": "Beroperasi tanpa air", "text_en": "Operate without water"}}', 'B', 'Marine Boiler'),
('Apa tujuan dari ''air vent cock'' di bagian atas drum uap boiler?', 'What is the purpose of the ''air vent cock'' on the top of a boiler steam drum?', '{"A": {"text": "Untuk mengambil sampel uap", "text_en": "To take a steam sample"}, "B": {"text": "Untuk melepaskan udara saat mengisi boiler dan saat start-up", "text_en": "To release air when filling the boiler and during start-up"}, "C": {"text": "Untuk menghubungkan pengukur tekanan", "text_en": "To connect a pressure gauge"}, "D": {"text": "Sebagai katup keselamatan cadangan", "text_en": "To act as a backup safety valve"}}', 'B', 'Marine Boiler'),
('Apa konsekuensi langsung dari ''kegagalan nyala api'' (flame failure) pada boiler yang bekerja secara otomatis?', 'What is the immediate consequence of a ''flame failure'' in an automatically fired boiler?', '{"A": {"text": "Alarm akan berbunyi tetapi boiler tetap menyala", "text_en": "An alarm will sound but the boiler stays on"}, "B": {"text": "Pasokan air akan berhenti", "text_en": "The water supply will stop"}, "C": {"text": "Pasokan bahan bakar segera dimatikan", "text_en": "The fuel supply is shut off immediately"}, "D": {"text": "Tekanan uap akan meningkat", "text_en": "The steam pressure will increase"}}', 'C', 'Marine Boiler'),
('Mengapa pengolahan kimia air umpan boiler diperlukan?', 'Why is chemical treatment of boiler feedwater necessary?', '{"A": {"text": "Untuk memberi warna pada air", "text_en": "To give color to the water"}, "B": {"text": "Untuk mencegah korosi, pembentukan kerak, dan mengontrol pH", "text_en": "To prevent corrosion, scale formation, and control pH"}, "C": {"text": "Untuk membuat air lebih cepat mendidih", "text_en": "To make the water boil faster"}, "D": {"text": "Hanya diperlukan untuk boiler pipa api", "text_en": "It is only required for fire-tube boilers"}}', 'B', 'Marine Boiler'),
('Apa itu ''propeller singing''?', 'What is ''propeller singing''?', '{"A": {"text": "Suara bernada tinggi yang disebabkan oleh aliran air di atas bilah baling-baling", "text_en": "A high-pitched noise caused by water flowing over the propeller blades"}, "B": {"text": "Alarm yang menandakan baling-baling rusak", "text_en": "An alarm that indicates a damaged propeller"}, "C": {"text": "Proses pemolesan baling-baling", "text_en": "The process of polishing a propeller"}, "D": {"text": "Getaran normal dari baling-baling", "text_en": "Normal vibration from the propeller"}}', 'A', 'Shafting & Propeller'),
('Apa tujuan dari ''gearbox'' dalam sistem propulsi dengan mesin diesel putaran menengah?', 'What is the purpose of a ''gearbox'' in a propulsion system with a medium-speed diesel engine?', '{"A": {"text": "Meningkatkan putaran mesin", "text_en": "To increase the engine RPM"}, "B": {"text": "Mengurangi RPM mesin yang tinggi ke RPM baling-baling yang lebih rendah dan efisien", "text_en": "To reduce the high engine RPM to a lower, efficient propeller RPM"}, "C": {"text": "Mendinginkan mesin", "text_en": "To cool the engine"}, "D": {"text": "Menghubungkan dua mesin menjadi satu", "text_en": "To connect two engines together"}}', 'B', 'Shafting & Propeller'),
('Anoda korban (sacrificial anodes) dipasang di area buritan kapal untuk...', 'Sacrificial anodes are fitted to the stern area of a ship to...', '{"A": {"text": "Menambah berat kapal", "text_en": "Add weight to the ship"}, "B": {"text": "Mencegah korosi galvanik pada lambung dan baling-baling", "text_en": "Prevent galvanic corrosion of the hull and propeller"}, "C": {"text": "Meningkatkan daya dorong", "text_en": "Increase thrust"}, "D": {"text": "Mengurangi getaran", "text_en": "Reduce vibration"}}', 'B', 'Shafting & Propeller'),
('Mengapa ''pemolesan baling-baling'' (propeller polishing) rutin dilakukan?', 'Why is regular ''propeller polishing'' performed?', '{"A": {"text": "Agar terlihat bagus", "text_en": "To make it look good"}, "B": {"text": "Untuk menyeimbangkan baling-baling", "text_en": "To balance the propeller"}, "C": {"text": "Untuk meningkatkan efisiensi dan mengurangi konsumsi bahan bakar dengan menciptakan permukaan yang halus", "text_en": "To improve efficiency and reduce fuel consumption by creating a smooth surface"}, "D": {"text": "Untuk mendeteksi keretakan", "text_en": "To detect cracks"}}', 'C', 'Shafting & Propeller'),
('Apa yang dimaksud dengan ''Blade Area Ratio'' (BAR)?', 'What does ''Blade Area Ratio'' (BAR) refer to?', '{"A": {"text": "Rasio antara panjang dan lebar bilah", "text_en": "The ratio of blade length to width"}, "B": {"text": "Rasio antara luas total bilah dengan luas piringan baling-baling", "text_en": "The ratio of the total blade area to the propeller disc area"}, "C": {"text": "Rasio antara berat baling-baling dan daya mesin", "text_en": "The ratio of propeller weight to engine power"}, "D": {"text": "Rasio kecepatan kapal terhadap kecepatan putaran baling-baling", "text_en": "The ratio of ship speed to propeller RPM"}}', 'B', 'Shafting & Propeller'),
('Fungsi utama dari ''air dryer'' dalam sistem udara bertekanan adalah...', 'The primary function of an ''air dryer'' in a compressed air system is...', '{"A": {"text": "Menghilangkan uap air dari udara", "text_en": "To remove water vapor from the air"}, "B": {"text": "Menghilangkan oli dari udara", "text_en": "To remove oil from the air"}, "C": {"text": "Mendinginkan udara", "text_en": "To cool the air"}, "D": {"text": "Menaikkan tekanan udara", "text_en": "To increase the air pressure"}}', 'A', 'Other Auxiliaries'),
('Apa tujuan dari ''homogenizer'' dalam sistem pengolahan bahan bakar?', 'What is the purpose of a ''homogenizer'' in a fuel treatment system?', '{"A": {"text": "Memisahkan air dari bahan bakar", "text_en": "To separate water from fuel"}, "B": {"text": "Memecah dan menyebarkan gugusan aspal dalam bahan bakar berat", "text_en": "To break down and disperse asphaltene clusters in heavy fuel oil"}, "C": {"text": "Mendinginkan bahan bakar", "text_en": "To cool the fuel"}, "D": {"text": "Mengukur viskositas bahan bakar", "text_en": "To measure fuel viscosity"}}', 'B', 'Other Auxiliaries'),
('Apa perbedaan fungsi utama antara ''bilge pump'' dan ''ballast pump''?', 'What is the main functional difference between a ''bilge pump'' and a ''ballast pump''?', '{"A": {"text": "Tidak ada perbedaan, keduanya dapat saling menggantikan", "text_en": "There is no difference, they are interchangeable"}, "B": {"text": "Bilge pump untuk air bersih, ballast pump untuk air kotor", "text_en": "Bilge pump is for clean water, ballast pump is for dirty water"}, "C": {"text": "Bilge pump membuang air yang tidak diinginkan dari ruang mesin, ballast pump memindahkan air untuk stabilitas", "text_en": "Bilge pump removes unwanted water from machinery spaces, ballast pump transfers water for stability"}, "D": {"text": "Bilge pump lebih besar dari ballast pump", "text_en": "Bilge pumps are larger than ballast pumps"}}', 'C', 'Other Auxiliaries'),
('Di mana lokasi khas ''generator darurat'' di kapal?', 'Where is the ''emergency generator'' typically located on a ship?', '{"A": {"text": "Di dalam ruang mesin utama", "text_en": "Inside the main engine room"}, "B": {"text": "Di dek paling bawah", "text_en": "On the lowest deck"}, "C": {"text": "Di luar ruang mesin utama, di dek atas", "text_en": "Outside the main engine room, on an upper deck"}, "D": {"text": "Di dekat anjungan", "text_en": "Next to the bridge"}}', 'C', 'Other Auxiliaries'),
('''Incinerator'' di kapal terutama digunakan untuk mematuhi MARPOL Annex mana?', 'An ''incinerator'' on a ship is primarily used to comply with which MARPOL annexes?', '{"A": {"text": "Annex IV (Sewage)", "text_en": "Annex IV (Sewage)"}, "B": {"text": "Annex VI (Air Pollution)", "text_en": "Annex VI (Air Pollution)"}, "C": {"text": "Annex V (Garbage) dan Annex I (Oil Sludge)", "text_en": "Annex V (Garbage) and Annex I (Oil Sludge)"}, "D": {"text": "Annex II (Noxious Liquid Substances)", "text_en": "Annex II (Noxious Liquid Substances"}}', 'C', 'Other Auxiliaries');