INSERT INTO public.teaching_content (topic_id, section_order, section_title, section_title_en, content) VALUES
('marine-engines', 1, 'Glosarium & Kosakata', 'Glossary & Vocabulary', '{"type": "glossary", "terms": [{"term": "Propulsion System", "def": "Sistem propulsi kapal yang menghasilkan daya dorong.", "def_en": "Ship''s propulsion system that generates thrust."}, {"term": "Combustion", "def": "Proses pembakaran bahan bakar untuk menghasilkan energi.", "def_en": "The process of burning fuel to produce energy."}, {"term": "Cylinder", "def": "Ruang di mana piston bergerak dan pembakaran terjadi.", "def_en": "A chamber where the piston moves and combustion occurs."}, {"term": "Piston", "def": "Komponen yang bergerak naik-turun di dalam silinder.", "def_en": "A component that moves up and down inside the cylinder."}, {"term": "Crankshaft", "def": "Poros yang mengubah gerakan linear piston menjadi gerakan rotasi.", "def_en": "A shaft that converts the linear motion of the piston into rotational motion."}, {"term": "Turbine", "def": "Mesin rotasi yang mengekstrak energi dari aliran fluida (uap atau gas).", "def_en": "A rotary engine that extracts energy from a fluid flow (steam or gas)."}, {"term": "Boiler", "def": "Perangkat yang menghasilkan uap dengan memanaskan air.", "def_en": "A device that generates steam by heating water."}, {"term": "Condenser", "def": "Penukar panas yang mengubah uap menjadi air.", "def_en": "A heat exchanger that converts steam into water."}, {"term": "Compressor", "def": "Perangkat yang meningkatkan tekanan gas.", "def_en": "A device that increases the pressure of a gas."}, {"term": "Combustion Chamber", "def": "Ruang di mana bahan bakar dibakar dengan udara bertekanan.", "def_en": "A chamber where fuel is burned with compressed air."}]}'),
('marine-engines', 2, 'Penjelasan Konsep', 'Concept Explanation', '{"type": "concepts", "items": [{"title": "1. Mesin Diesel Laut / Marine Diesel Engines", "paragraphs": ["Mesin diesel laut adalah mesin pembakaran internal yang menggunakan kompresi udara untuk menyalakan bahan bakar. Mesin ini sangat umum digunakan pada kapal karena efisiensinya yang tinggi dan torsi yang kuat.", "Marine diesel engines are internal combustion engines that use compressed air to ignite fuel. They are widely used in ships due to their high efficiency and strong torque."], "points": [{"main": "Prinsip Kerja / Working Principle: Udara dikompresi hingga suhu tinggi, kemudian bahan bakar disemprotkan dan terbakar secara spontan.", "en": "Air is compressed to a high temperature, then fuel is injected and ignites spontaneously."}, {"main": "Komponen Utama / Main Components: Silinder, Piston, Batang Penghubung (Connecting Rod), Poros Engkol (Crankshaft), Sistem Injeksi Bahan Bakar, Sistem Buang, Sistem Pendingin, Sistem Pelumasan.", "en": "Cylinder, Piston, Connecting Rod, Crankshaft, Fuel Injection System, Exhaust System, Cooling System, Lubrication System."}, {"main": "Jenis / Types:", "sub_points": [{"main": "2-Tak (Two-Stroke): Pembakaran terjadi setiap putaran poros engkol. Lebih sederhana, lebih ringan, daya per volume lebih tinggi.", "en": "2-Stroke: Combustion occurs every crankshaft revolution. Simpler, lighter, higher power-to-volume ratio."}, {"main": "4-Tak (Four-Stroke): Pembakaran terjadi setiap dua putaran poros engkol (Intake, Compression, Power, Exhaust). Lebih efisien bahan bakar, emisi lebih rendah.", "en": "4-Stroke: Combustion occurs every two crankshaft revolutions (Intake, Compression, Power, Exhaust). More fuel-efficient, lower emissions."}]}, {"main": "Kelebihan / Advantages: Efisiensi bahan bakar tinggi, torsi besar, keandalan.", "en": "High fuel efficiency, high torque, reliability."}, {"main": "Kekurangan / Disadvantages: Getaran dan kebisingan, perawatan lebih kompleks.", "en": "Vibration and noise, more complex maintenance."}]}, {"title": "2. Turbin Uap Laut / Marine Steam Turbines", "paragraphs": ["Turbin uap laut menggunakan uap bertekanan tinggi yang dihasilkan oleh boiler untuk memutar bilah turbin, yang kemudian menggerakkan poros baling-baling. Umumnya ditemukan pada kapal besar seperti kapal tanker dan kapal induk.", "Marine steam turbines use high-pressure steam generated by a boiler to rotate turbine blades, which then drive the propeller shaft. Commonly found in large vessels like tankers and aircraft carriers."], "points": [{"main": "Prinsip Kerja / Working Principle: Air dipanaskan di boiler menjadi uap, uap mengembang melalui turbin, memutar bilah, lalu uap dikondensasi kembali menjadi air.", "en": "Water is heated in a boiler to steam, steam expands through the turbine, rotating the blades, then the steam is condensed back into water."}, {"main": "Komponen Utama / Main Components: Boiler (Ketel Uap), Turbin, Kondensor, Pompa Air Umpan (Feed Pump).", "en": "Boiler, Turbine, Condenser, Feed Pump."}, {"main": "Kelebihan / Advantages: Operasi yang halus dan minim getaran, dapat menggunakan berbagai jenis bahan bakar, daya tinggi.", "en": "Smooth and low-vibration operation, can use various types of fuel, high power output."}, {"main": "Kekurangan / Disadvantages: Kompleksitas sistem, waktu start-up yang lebih lama, efisiensi lebih rendah pada beban parsial.", "en": "System complexity, longer start-up time, lower efficiency at partial loads."}]}, {"title": "3. Turbin Gas Laut / Marine Gas Turbines", "paragraphs": ["Turbin gas laut adalah mesin pembakaran internal rotasi yang menggunakan gas panas bertekanan tinggi untuk memutar turbin. Sering digunakan pada kapal perang dan kapal pesiar cepat karena rasio daya terhadap beratnya yang tinggi.", "Marine gas turbines are rotary internal combustion engines that use high-pressure hot gas to rotate a turbine. Often used in warships and fast cruise ships due to their high power-to-weight ratio."], "points": [{"main": "Prinsip Kerja / Working Principle: Udara dikompresi, dicampur dengan bahan bakar dan dibakar di ruang bakar, gas panas yang dihasilkan memutar turbin.", "en": "Air is compressed, mixed with fuel and burned in a combustion chamber, the resulting hot gas rotates the turbine."}, {"main": "Komponen Utama / Main Components: Kompresor, Ruang Bakar (Combustion Chamber), Turbin.", "en": "Compressor, Combustion Chamber, Turbine."}, {"main": "Kelebihan / Advantages: Rasio daya terhadap berat yang tinggi, start-up cepat, getaran rendah.", "en": "High power-to-weight ratio, fast start-up, low vibration."}, {"main": "Kekurangan / Disadvantages: Efisiensi bahan bakar lebih rendah pada beban parsial, sensitif terhadap kualitas udara, biaya bahan bakar lebih tinggi.", "en": "Lower fuel efficiency at partial loads, sensitive to air quality, higher fuel costs."}]}]}'),
('marine-engines', 3, 'Perbandingan Sistem Propulsi', 'Comparison of Propulsion Systems', '{"type": "comparison_table", "headers": ["Fitur / Feature", "Mesin Diesel / Diesel Engine", "Turbin Uap / Steam Turbine", "Turbin Gas / Gas Turbine"], "rows": [["Efisiensi Bahan Bakar / Fuel Efficiency", "Tinggi / High", "Sedang / Moderate", "Sedang-Rendah / Moderate-Low"], ["Ukuran & Berat / Size & Weight", "Besar & Berat / Large & Heavy", "Sangat Besar & Berat / Very Large & Heavy", "Kompak & Ringan / Compact & Light"], ["Waktu Start-up / Start-up Time", "Cepat / Fast", "Lama / Long", "Sangat Cepat / Very Fast"], ["Getaran & Kebisingan / Vibration & Noise", "Sedang-Tinggi / Moderate-High", "Rendah / Low", "Rendah / Low"], ["Jenis Bahan Bakar / Fuel Type", "Diesel, HFO / Diesel, HFO", "Beragam (HFO, Gas, Nuklir) / Various (HFO, Gas, Nuclear)", "Diesel, Gas / Diesel, Gas"]]}'),
('marine-engines', 4, 'Fokus Tata Bahasa', 'Grammar Focus', '{"type": "grammar", "items": [{"title": "1. Simple Present Tense (for Functions and Facts)", "explanation": "Kita menggunakan Simple Present Tense untuk menjelaskan fungsi komponen dan fakta umum tentang cara kerja mesin. / We use the Simple Present Tense to explain the function of components and general facts about how machinery works.", "examples": ["A crankshaft <strong>converts</strong> the linear motion of the piston into rotational motion.", "A two-stroke engine <strong>completes</strong> its power cycle in one revolution.", "Steam turbines <strong>use</strong> high-pressure steam to rotate the blades."]}, {"title": "2. Passive Voice (for Processes)", "explanation": "Kita sering menggunakan Passive Voice (Subject + to be + Verb 3) ketika fokusnya adalah pada prosesnya, bukan siapa yang melakukan tindakan. / We often use the Passive Voice (Subject + to be + Verb 3) when the focus is on the process, not who is doing the action.", "examples": ["Air <strong>is compressed</strong> to a high temperature.", "Fuel <strong>is injected</strong> into the cylinder.", "Water <strong>is heated</strong> in a boiler to become steam."]}]}'),
('marine-engines', 5, 'Contoh Percakapan', 'Conversation Example', '{"type": "conversation", "scenario": "Skenario: Dua perwira mesin sedang mendiskusikan jenis mesin utama di kapal mereka.", "scenario_en": "Scenario: Two engineering officers are discussing the main engine types on their vessel.", "lines": [{"speaker": "Officer A", "dialogue": "Good morning, Chief. I was reviewing the engine specifications. Our vessel is equipped with a two-stroke marine diesel engine, correct?", "dialogue_en": "Selamat pagi, Chief. Saya sedang meninjau spesifikasi mesin. Kapal kita dilengkapi dengan mesin diesel laut dua tak, benar?"}, {"speaker": "Officer B", "dialogue": "That''s right, Officer. It''s a reliable unit. Do you know the main difference between a two-stroke and a four-stroke engine?", "dialogue_en": "Betul, Officer. Ini unit yang handal. Apakah Anda tahu perbedaan utama antara mesin dua tak dan empat tak?"}, {"speaker": "Officer A", "dialogue": "Yes, Chief. A two-stroke engine completes its power cycle in one crankshaft revolution, while a four-stroke takes two revolutions. Two-strokes are generally more powerful for their size, but four-strokes are often more fuel-efficient.", "dialogue_en": "Ya, Chief. Mesin dua tak menyelesaikan siklus dayanya dalam satu putaran poros engkol, sedangkan empat tak membutuhkan dua putaran. Mesin dua tak umumnya lebih bertenaga untuk ukurannya, tetapi empat tak seringkali lebih hemat bahan bakar."}, {"speaker": "Officer B", "dialogue": "Excellent. And what about steam turbines? Have you studied them?", "dialogue_en": "Bagus sekali. Dan bagaimana dengan turbin uap? Apakah Anda sudah mempelajarinya?"}, {"speaker": "Officer A", "dialogue": "Yes, Chief. Steam turbines use high-pressure steam from a boiler to turn the turbine blades. They are known for their smooth operation and are common on large vessels like LNG carriers or older tankers. Gas turbines, on the other hand, are more compact and offer quick start-up, often found on fast ferries or naval ships.", "dialogue_en": "Ya, Chief. Turbin uap menggunakan uap bertekanan tinggi dari boiler untuk memutar bilah turbin. Mereka dikenal karena operasinya yang halus dan umum ditemukan pada kapal besar seperti kapal pengangkut LNG atau tanker tua. Turbin gas, di sisi lain, lebih kompak dan menawarkan start-up cepat, sering ditemukan pada feri cepat atau kapal angkatan laut."}, {"speaker": "Officer B", "dialogue": "Very good, Officer. It''s crucial to understand the characteristics of each propulsion system. Keep up the good work.", "dialogue_en": "Sangat bagus, Officer. Penting untuk memahami karakteristik setiap sistem propulsi. Teruslah bekerja dengan baik."}]}'),
('marine-boiler', 1, 'Glosarium & Kosakata', 'Glossary & Vocabulary', '{"type": "glossary", "terms": [{"term": "Boiler", "def": "Perangkat tertutup untuk menghasilkan uap dengan memanaskan air.", "def_en": "A closed vessel for generating steam by heating water."}, {"term": "Furnace", "def": "Ruang di dalam boiler tempat bahan bakar dibakar.", "def_en": "The chamber within a boiler where fuel is burned."}, {"term": "Steam Drum", "def": "Bejana di bagian atas boiler tempat uap dipisahkan dari air.", "def_en": "A vessel at the top of the boiler where steam is separated from water."}, {"term": "Water Tubes", "def": "Pipa-pipa yang berisi air dan dipanaskan oleh gas panas dari luar.", "def_en": "Tubes that contain water and are heated by hot gases from the outside."}, {"term": "Fire Tubes", "def": "Pipa-pipa yang dialiri gas panas dan dikelilingi oleh air.", "def_en": "Tubes through which hot gases pass and are surrounded by water."}, {"term": "Safety Valve", "def": "Katup yang secara otomatis melepaskan tekanan berlebih.", "def_en": "A valve that automatically releases excess pressure."}, {"term": "Gauge Glass", "def": "Gelas penduga untuk menunjukkan level air di dalam boiler.", "def_en": "A glass tube to indicate the water level inside the boiler."}, {"term": "Feedwater", "def": "Air yang diumpankan ke dalam boiler untuk diubah menjadi uap.", "def_en": "Water that is fed into the boiler to be converted into steam."}, {"term": "Blowdown", "def": "Proses membuang sebagian air boiler untuk mengurangi konsentrasi kotoran.", "def_en": "The process of draining some boiler water to reduce impurity concentration."}, {"term": "Mountings / Fittings", "def": "Perlengkapan yang dipasang pada boiler untuk keselamatan dan operasi.", "def_en": "Fittings attached to the boiler for safety and operation."}]}'),
('marine-boiler', 2, 'Penjelasan Konsep', 'Concept Explanation', '{"type": "concepts", "items": [{"title": "1. Pengantar Ketel Uap Kapal / Introduction to Marine Boilers", "paragraphs": ["Ketel uap kapal (boiler) adalah komponen vital di banyak kapal, terutama yang menggunakan turbin uap atau membutuhkan uap untuk berbagai layanan bantu (auxiliary services) seperti pemanasan kargo, pemanas bahan bakar, dan untuk keperluan akomodasi. Fungsi utamanya adalah mengubah air menjadi uap bertekanan tinggi melalui proses pembakaran bahan bakar.", "A marine boiler is a vital component on many ships, especially those using steam turbines or requiring steam for various auxiliary services like cargo heating, fuel heating, and for accommodation purposes. Its primary function is to convert water into high-pressure steam through the process of fuel combustion."], "points": []}, {"title": "2. Jenis-jenis Ketel Uap / Types of Boilers", "paragraphs": ["Dua jenis utama ketel uap yang digunakan di kapal adalah ketel uap pipa api (fire-tube) dan pipa air (water-tube).", "The two main types of boilers used on ships are fire-tube and water-tube boilers."], "sub_sections": [{"title": "A. Ketel Uap Pipa Api / Fire-Tube Boiler", "paragraphs": ["Pada jenis ini, gas panas dari tungku pembakaran mengalir melalui pipa-pipa (fire tubes) yang terendam di dalam air di dalam sebuah shell (bejana). Panas dari gas ditransfer melalui dinding pipa ke air di sekitarnya, mengubahnya menjadi uap.", "In this type, hot gases from the combustion furnace flow through tubes (fire tubes) that are submerged in water within a shell. Heat from the gases is transferred through the tube walls to the surrounding water, turning it into steam."], "points": [{"main": "Kelebihan / Advantages: Desain sederhana, kuat, biaya awal lebih rendah, perawatan lebih mudah.", "en": "Simple design, robust, lower initial cost, easier maintenance."}, {"main": "Kekurangan / Disadvantages: Tekanan uap terbatas (biasanya di bawah 20 bar), kapasitas uap lebih kecil, lebih berat untuk kapasitas yang sama, waktu start-up lebih lama.", "en": "Limited steam pressure (usually below 20 bar), smaller steam capacity, heavier for the same capacity, longer start-up time."}, {"main": "Aplikasi / Application: Umumnya digunakan sebagai ketel uap bantu (auxiliary boiler) pada kapal motor.", "en": "Commonly used as auxiliary boilers on motor vessels."}]}, {"title": "B. Ketel Uap Pipa Air / Water-Tube Boiler", "paragraphs": ["Pada jenis ini, air mengalir di dalam pipa-pipa (water tubes), sementara gas panas dari pembakaran bersirkulasi di luar pipa-pipa tersebut. Desain ini memungkinkan produksi uap dengan tekanan dan suhu yang jauh lebih tinggi.", "In this type, water flows inside the tubes (water tubes), while hot gases from combustion circulate outside these tubes. This design allows for the production of steam at much higher pressures and temperatures."], "points": [{"main": "Kelebihan / Advantages: Mampu menghasilkan uap bertekanan sangat tinggi (di atas 60 bar), kapasitas uap besar, start-up lebih cepat, lebih efisien.", "en": "Capable of generating very high-pressure steam (above 60 bar), large steam capacity, faster start-up, more efficient."}, {"main": "Kekurangan / Disadvantages: Desain lebih kompleks, biaya lebih tinggi, membutuhkan kualitas air umpan yang sangat baik.", "en": "More complex design, higher cost, requires excellent feedwater quality."}, {"main": "Aplikasi / Application: Digunakan sebagai ketel uap utama (main boiler) pada kapal bertenaga uap (misalnya, kapal tanker LNG) dan kapal perang.", "en": "Used as main boilers on steam-powered ships (e.g., LNG carriers) and naval vessels."}]}]}, {"title": "3. Komponen Utama & Perlengkapan Keselamatan / Main Components & Safety Fittings", "paragraphs": ["Setiap boiler dilengkapi dengan berbagai perlengkapan (mountings/fittings) untuk memastikan operasi yang aman dan efisien.", "Every boiler is equipped with various mountings/fittings to ensure safe and efficient operation."], "points": [{"main": "Safety Valves (Katup Keselamatan): Untuk melepaskan tekanan uap berlebih secara otomatis. Biasanya ada dua.", "en": "To automatically release excess steam pressure. Usually, there are two."}, {"main": "Main Steam Stop Valve (Katup Stop Uap Utama): Untuk mengontrol aliran uap dari boiler ke sistem.", "en": "To control the flow of steam from the boiler to the system."}, {"main": "Feed Check Valves (Katup Cek Umpan): Untuk mengontrol pasokan air umpan ke boiler dan mencegah aliran balik.", "en": "To control the supply of feedwater to the boiler and prevent backflow."}, {"main": "Water Level Gauge (Gelas Penduga Level Air): Untuk memantau level air di dalam boiler secara visual.", "en": "To visually monitor the water level inside the boiler."}, {"main": "Pressure Gauge (Pengukur Tekanan): Untuk menunjukkan tekanan uap di dalam boiler.", "en": "To indicate the steam pressure inside the boiler."}, {"main": "Blowdown Valve (Katup Buang): Untuk membuang endapan dan lumpur dari bagian bawah boiler.", "en": "To discharge sludge and sediment from the bottom of the boiler."}]}]}'),
('marine-boiler', 3, 'Fokus Tata Bahasa', 'Grammar Focus', '{"type": "grammar", "items": [{"title": "1. Modal Verbs for Safety & Obligation (must, should)", "explanation": "Kita menggunakan ''must'' untuk kewajiban yang sangat penting dan ''should'' untuk rekomendasi atau praktik terbaik. / We use ''must'' for critical obligations and ''should'' for recommendations or best practices.", "examples": ["You <strong>must</strong> shut down the burner if you cannot see the water level. (Kewajiban)", "The safety valves <strong>must</strong> be tested regularly. (Peraturan)", "You <strong>should</strong> wear your PPE before performing a blowdown. (Rekomendasi kuat)", "The boiler water <strong>should</strong> be tested daily. (Praktik terbaik)"]}, {"title": "2. Prepositions of Place (in, on, inside, outside)", "explanation": "Preposisi membantu kita menjelaskan lokasi komponen secara akurat. / Prepositions help us describe the location of components accurately.", "examples": ["In a fire-tube boiler, hot gas flows <strong>inside</strong> the tubes.", "In a water-tube boiler, water is <strong>in</strong> the tubes and hot gas is <strong>outside</strong>.", "The safety valves are located <strong>on</strong> top of the steam drum."]}]}'),
('marine-boiler', 4, 'Contoh Percakapan', 'Conversation Example', '{"type": "conversation", "scenario": "Skenario: Chief Engineer sedang memberikan instruksi kepada Junior Engineer tentang pemeriksaan boiler bantu.", "scenario_en": "Scenario: The Chief Engineer is giving instructions to a Junior Engineer about checking the auxiliary boiler.", "lines": [{"speaker": "Chief Engineer", "dialogue": "Junior, before we start the cargo heating, I need you to perform a routine check on the auxiliary boiler. First, check the water level in the gauge glass. What do you see?", "dialogue_en": "Junior, sebelum kita mulai pemanasan kargo, saya mau kamu melakukan pemeriksaan rutin pada boiler bantu. Pertama, periksa level air di gelas penduga. Apa yang kamu lihat?"}, {"speaker": "Junior Engineer", "dialogue": "Chief, the water level is normal, about halfway up the glass. The pressure gauge reads 7 bar.", "dialogue_en": "Chief, level air normal, sekitar setengah dari gelas. Pengukur tekanan menunjukkan 7 bar."}, {"speaker": "Chief Engineer", "dialogue": "Good. That''s the correct operating pressure for this fire-tube boiler. Now, I want you to test the safety valves. Just ease the lifting gear, don''t pop them fully. We need to ensure they are not stuck.", "dialogue_en": "Bagus. Itu tekanan operasi yang benar untuk boiler pipa api ini. Sekarang, saya mau kamu menguji katup keselamatan. Cukup angkat tuasnya sedikit, jangan dibuka penuh. Kita perlu memastikan katupnya tidak macet."}, {"speaker": "Junior Engineer", "dialogue": "Understood, Chief. After that, should I perform a quick blowdown?", "dialogue_en": "Dimengerti, Chief. Setelah itu, apakah saya perlu melakukan blowdown singkat?"}, {"speaker": "Chief Engineer", "dialogue": "Yes, a short scum blowdown and then a bottom blowdown. Just for a few seconds each. It''s important to remove any impurities from the water surface and the bottom of the shell. Safety first, always wear your PPE.", "dialogue_en": "Ya, lakukan scum blowdown singkat lalu bottom blowdown. Cukup beberapa detik saja masing-masing. Penting untuk membuang kotoran dari permukaan air dan dasar shell. Utamakan keselamatan, selalu kenakan APD-mu."}]}'),
('shafting-installations', 1, 'Glosarium & Kosakata', 'Glossary & Vocabulary', '{"type": "glossary", "terms": [{"term": "Shafting", "def": "Sistem poros yang mentransmisikan putaran dari mesin ke baling-baling.", "def_en": "The system of shafts that transmits rotation from the engine to the propeller."}, {"term": "Propeller", "def": "Perangkat dengan daun (blades) yang berputar untuk menghasilkan daya dorong.", "def_en": "A device with blades that rotates to produce thrust."}, {"term": "Thrust Block", "def": "Bantalan yang menyalurkan daya dorong dari poros ke badan kapal.", "def_en": "A bearing that transfers the thrust from the shaft to the ship''s hull."}, {"term": "Stern Tube", "def": "Tabung yang melingkupi poros baling-baling saat menembus badan kapal.", "def_en": "A tube housing the propeller shaft where it passes through the hull."}, {"term": "Plummer Block", "def": "Bantalan untuk menopang poros antara (intermediate shaft).", "def_en": "A bearing that supports the intermediate shaft."}, {"term": "Alignment", "def": "Kelurusan poros untuk mencegah getaran dan keausan berlebih.", "def_en": "The straightness of the shafting to prevent vibration and excessive wear."}, {"term": "Fixed Pitch Propeller (FPP)", "def": "Baling-baling dengan sudut daun yang tidak bisa diubah.", "def_en": "A propeller with a blade angle that cannot be changed."}, {"term": "Controllable Pitch Propeller (CPP)", "def": "Baling-baling yang sudut daunnya bisa diubah saat beroperasi.", "def_en": "A propeller whose blade angle can be changed during operation."}]}'),
('shafting-installations', 2, 'Penjelasan Konsep', 'Concept Explanation', '{"type": "concepts", "items": [{"title": "1. Fungsi Sistem Poros / Function of the Shafting System", "paragraphs": ["Sistem poros memiliki dua fungsi utama: 1) Mentransmisikan torsi (gaya putar) dari mesin utama ke baling-baling. 2) Mentransmisikan daya dorong (thrust) yang dihasilkan oleh baling-baling ke struktur kapal untuk mendorongnya maju atau mundur.", "The shafting system has two main functions: 1) To transmit torque (rotational force) from the main engine to the propeller. 2) To transmit the thrust generated by the propeller to the ship''s structure to push it forward or backward."], "points": []}, {"title": "2. Komponen Utama Instalasi Poros / Main Components of a Shafting Installation", "paragraphs": [], "points": [{"main": "Thrust Shaft & Block: Poros ini terhubung langsung ke mesin dan memiliki bantalan pendorong (thrust block) yang besar untuk menyerap daya dorong.", "en": "This shaft is connected directly to the engine and has a large thrust block to absorb the thrust force."}, {"main": "Intermediate Shaft(s): Satu atau lebih poros yang menghubungkan poros pendorong dengan poros ekor. Poros ini ditopang oleh bantalan yang disebut plummer blocks.", "en": "One or more shafts that connect the thrust shaft to the tail shaft. These shafts are supported by bearings called plummer blocks."}, {"main": "Propeller Shaft (Tail Shaft): Poros terakhir dalam rangkaian, yang melewati tabung buritan (stern tube) dan tempat baling-baling dipasang.", "en": "The final shaft in the line, which passes through the stern tube and on which the propeller is mounted."}, {"main": "Stern Tube: Sebuah tabung kedap air yang berisi bantalan dan perapat (seals) untuk menopang poros baling-baling dan mencegah air laut masuk ke kamar mesin.", "en": "A watertight tube containing bearings and seals to support the propeller shaft and prevent seawater from entering the engine room."}]}, {"title": "3. Jenis-jenis Baling-Baling / Propeller Types", "paragraphs": [], "sub_sections": [{"title": "A. Fixed Pitch Propeller (FPP)", "paragraphs": ["Sudut (pitch) dari daun baling-balingnya tetap dan tidak bisa diubah. Untuk bergerak mundur, arah putaran mesin harus dibalik. Ini adalah jenis yang paling umum karena sederhana, kuat, dan efisien.", "The angle (pitch) of its blades is fixed and cannot be changed. To move astern, the engine''s direction of rotation must be reversed. This is the most common type due to its simplicity, robustness, and efficiency."], "points": []}, {"title": "B. Controllable Pitch Propeller (CPP)", "paragraphs": ["Sudut daun baling-balingnya dapat diubah menggunakan sistem hidrolik, bahkan saat mesin berputar. Ini memungkinkan kontrol kecepatan dan arah (maju/mundur) yang sangat baik tanpa mengubah putaran atau arah mesin. Sangat berguna untuk kapal yang sering bermanuver, seperti feri atau kapal tunda.", "The pitch of the blades can be changed using a hydraulic system, even while the engine is running. This allows for excellent control of speed and direction (ahead/astern) without changing the engine''s speed or direction. Very useful for vessels that maneuver frequently, like ferries or tugboats."], "points": []}]}]}'),
('shafting-installations', 3, 'Fokus Tata Bahasa', 'Grammar Focus', '{"type": "grammar", "items": [{"title": "1. Cause and Effect (If..., causes, leads to)", "explanation": "Kita menggunakan struktur ini untuk menjelaskan hubungan sebab-akibat, yang sangat umum dalam bidang teknik. / We use these structures to explain cause-and-effect relationships, which are very common in engineering.", "examples": ["<strong>If</strong> the shaft alignment is poor, it <strong>will cause</strong> excessive vibration.", "Poor alignment <strong>causes</strong> premature bearing wear.", "A damaged propeller blade <strong>leads to</strong> a loss of efficiency."]}, {"title": "2. Comparative Adjectives (more, -er, than)", "explanation": "Kita menggunakan kata sifat komparatif untuk membandingkan dua hal, seperti FPP dan CPP. / We use comparative adjectives to compare two things, such as an FPP and a CPP.", "examples": ["An FPP is <strong>simpler</strong> and <strong>stronger than</strong> a CPP.", "A CPP is <strong>more expensive</strong> and <strong>more complex</strong>.", "A CPP offers <strong>better</strong> maneuverability. (better is the irregular comparative of good)"]}]}'),
('shafting-installations', 4, 'Contoh Percakapan', 'Conversation Example', '{"type": "conversation", "scenario": "Skenario: Second Engineer dan Oiler sedang melakukan inspeksi di terowongan poros (shaft tunnel).", "scenario_en": "Scenario: The Second Engineer and an Oiler are on an inspection round in the shaft tunnel.", "lines": [{"speaker": "Second Engineer", "dialogue": "Alright, let''s check the plummer blocks. Feel the bearing housing. Is it running hot?", "dialogue_en": "Baik, mari kita periksa plummer block. Rasakan rumah bantalannya. Apakah panas saat beroperasi?"}, {"speaker": "Oiler", "dialogue": "No, sir. The temperature feels normal. The oil level in the sight glass is also good.", "dialogue_en": "Tidak, Pak. Suhunya terasa normal. Level oli di gelas penduga juga bagus."}, {"speaker": "Second Engineer", "dialogue": "Good. Now, let''s move to the stern tube forward seal. Shine your torch on it. Any signs of water leakage?", "dialogue_en": "Bagus. Sekarang, kita pindah ke perapat depan tabung buritan. Sorot dengan sentermu. Ada tanda-tanda kebocoran air?"}, {"speaker": "Oiler", "dialogue": "It looks dry, sir. No leakage.", "dialogue_en": "Kelihatannya kering, Pak. Tidak ada kebocoran."}, {"speaker": "Second Engineer", "dialogue": "Excellent. Proper shaft alignment is critical. If we see high temperatures or leaks, it could be a sign of misalignment, which can cause serious damage. We''ll check the aft seal from the deck later.", "dialogue_en": "Bagus sekali. Kelurusan poros yang baik itu sangat penting. Jika kita melihat suhu tinggi atau kebocoran, itu bisa jadi tanda kelurusan yang buruk, yang dapat menyebabkan kerusakan serius. Nanti kita periksa perapat belakang dari dek."}]}'),
('other-auxiliaries', 1, 'Glosarium & Kosakata', 'Glossary & Vocabulary', '{"type": "glossary", "terms": [{"term": "Pump", "def": "Pompa, alat untuk memindahkan fluida.", "def_en": "A device used to move fluids."}, {"term": "Impeller", "def": "Roda berputar di dalam pompa sentrifugal yang mendorong fluida.", "def_en": "The rotating part of a centrifugal pump that moves the fluid."}, {"term": "Separator", "def": "Mesin yang memisahkan zat berdasarkan massa jenis menggunakan gaya sentrifugal.", "def_en": "A machine that separates substances of different densities using centrifugal force."}, {"term": "Compressor", "def": "Mesin yang menaikkan tekanan udara atau gas.", "def_en": "A machine that increases the pressure of air or gas."}, {"term": "Heat Exchanger", "def": "Alat untuk mentransfer panas antara dua fluida tanpa mencampurnya.", "def_en": "A device to transfer heat between two fluids without mixing them."}, {"term": "Distillation", "def": "Proses pemurnian cairan dengan pemanasan dan pendinginan (penyulingan).", "def_en": "The process of purifying a liquid by heating and cooling."}, {"term": "Refrigerant", "def": "Zat yang digunakan dalam siklus pendinginan untuk menyerap dan melepaskan panas.", "def_en": "A substance used in a refrigeration cycle to absorb and release heat."}, {"term": "Gravity Disc", "def": "Cincin di dalam purifier yang menentukan posisi antarmuka air-oli.", "def_en": "A ring inside a purifier that determines the position of the water-oil interface."}]}'),
('other-auxiliaries', 2, 'Penjelasan Konsep', 'Concept Explanation', '{"type": "concepts", "items": [{"title": "1. Pompa-pompa / Pumps", "paragraphs": ["Pompa sangat penting untuk memindahkan berbagai jenis cairan di sekitar kapal untuk berbagai sistem. / Pumps are essential for moving various types of liquids around the ship for numerous systems."], "sub_sections": [{"title": "A. Pompa Sentrifugal / Centrifugal Pump", "paragraphs": ["Prinsip kerjanya menggunakan impeler (impeller) yang berputar cepat untuk melemparkan fluida ke luar karena gaya sentrifugal. Fluida kemudian dikumpulkan di dalam rumah pompa (casing) dan diarahkan ke saluran keluar (discharge)."], "points": [{"main": "Karakteristik: Ideal untuk memindahkan volume cairan yang besar pada tekanan yang relatif rendah. Alirannya lancar dan tidak berdenyut."}, {"main": "Aplikasi Umum: Pompa Pendingin Air Laut (Sea Water Cooling Pumps), Pompa Ballast (Ballast Pumps), Pompa Pemadam Kebakaran (Fire Pumps)."}]}, {"title": "B. Pompa Perpindahan Positif / Positive Displacement Pump", "paragraphs": ["Prinsip kerjanya adalah ''menjebak'' sejumlah volume fluida dan ''memaksanya'' pindah dari sisi isap (suction) ke sisi keluar (discharge). Alirannya hampir konstan terlepas dari tekanan."], "points": [{"main": "Karakteristik: Mampu menghasilkan tekanan tinggi, cocok untuk cairan kental (viscous), dan seringkali dapat menghisap sendiri (self-priming)."}, {"main": "Sub-jenis & Aplikasi Umum:", "sub_points": [{"main": "Pompa Roda Gigi (Gear Pump): Digunakan untuk oli pelumas dan transfer bahan bakar."}, {"main": "Pompa Ulir (Screw Pump): Digunakan untuk transfer bahan bakar dan oli pelumas, menghasilkan aliran yang sangat halus."}, {"main": "Pompa Piston (Piston Pump): Digunakan untuk aplikasi tekanan sangat tinggi seperti sistem hidrolik."}]}]}]}, {"title": "2. Kompresor Udara / Air Compressor", "paragraphs": ["Menghasilkan udara bertekanan tinggi yang disimpan dalam tabung udara (air receivers) untuk berbagai keperluan.", "Produces high-pressure air stored in air receivers for various purposes."], "points": [{"main": "Udara Start (Starting Air): Tekanan sangat tinggi (sekitar 30 bar) untuk menyalakan mesin diesel utama dan generator."}, {"main": "Udara Servis/Kerja (Service/Working Air): Tekanan lebih rendah (sekitar 7-10 bar) untuk peralatan pneumatik, pembersihan, dll."}, {"main": "Udara Kontrol (Control Air): Udara yang sangat kering dan bersih untuk sistem kontrol otomatis."}]}, {"title": "3. Pemisah Sentrifugal (Purifier & Clarifier) / Centrifugal Separator", "paragraphs": ["Mesin ini menggunakan gaya sentrifugal (ribuan kali gaya gravitasi) untuk memisahkan zat dengan massa jenis berbeda. Tujuan utamanya adalah membersihkan bahan bakar dan oli pelumas dari kontaminan berbahaya seperti air dan lumpur (sludge).", "This machine uses centrifugal force (thousands of times gravity) to separate substances of different densities. Its main purpose is to clean fuel and lubricating oil from harmful contaminants like water and sludge."], "sub_sections": [{"title": "Komponen Utama / Main Components", "points": [{"main": "Bowl: Komponen berputar berkecepatan tinggi tempat pemisahan terjadi."}, {"main": "Disc Stack: Tumpukan piringan berbentuk kerucut di dalam bowl untuk memperluas area permukaan dan meningkatkan efisiensi pemisahan."}, {"main": "Motor & Gear: Menggerakkan bowl pada kecepatan tinggi (misalnya 6.000 - 10.000 RPM)."}, {"main": "Gravity Disc (atau Dam Ring): Komponen kunci yang digunakan dalam mode purifier untuk mengatur posisi antarmuka (interface) antara oli dan air."}]}, {"title": "Persamaan & Perbedaan / Similarities & Differences", "paragraphs": ["Mesin yang sama dapat diatur sebagai Purifier atau Clarifier."], "points": [{"main": "Persamaan: Keduanya menggunakan prinsip sentrifugal, memiliki konstruksi dasar yang sama, dan bertujuan untuk membersihkan cairan."}, {"main": "Perbedaan (Mode Operasi):", "sub_points": [{"main": "Mode Purifier: Dikonfigurasi untuk memisahkan <strong>dua cairan</strong> yang tidak dapat bercampur (misalnya, oli dan air) dan sejumlah kecil padatan. Memiliki <strong>dua saluran keluar cairan</strong> (satu untuk oli bersih, satu untuk air) dan satu saluran keluar padatan. Menggunakan <strong>gravity disc</strong>."}, {"main": "Mode Clarifier: Dikonfigurasi untuk memisahkan sejumlah kecil <strong>padatan</strong> dari <strong>satu cairan</strong>. Hanya memiliki <strong>satu saluran keluar cairan</strong> dan satu saluran keluar padatan. Tidak menggunakan gravity disc."}]}]}]}, {"title": "4. Generator Air Tawar / Fresh Water Generator (FWG)", "paragraphs": ["Membuat air tawar dari air laut, biasanya menggunakan metode distilasi vakum suhu rendah untuk efisiensi energi.", "Produces fresh water from sea water, typically using low-temperature vacuum distillation for energy efficiency."], "sub_sections": [{"title": "Proses Distilasi Vakum / Vacuum Distillation Process", "points": [{"main": "Pemanasan: Panas buangan dari air pendingin jaket mesin utama (sekitar 70-80°C) dialirkan ke evaporator."}, {"main": "Penguapan Vakum: Air laut diumpankan ke evaporator yang dijaga dalam kondisi vakum tinggi. Dalam vakum, air mendidih pada suhu rendah (misalnya 40-60°C), bukan 100°C."}, {"main": "Pemisahan Uap: Uap air murni naik, melewati demister (pemisah tetesan) untuk menghilangkan tetesan air garam."}, {"main": "Kondensasi: Uap murni kemudian mengalir ke kondensor, yang didinginkan oleh air laut dingin. Uap mengembun menjadi air tawar murni (distilat)."}, {"main": "Ekstraksi: Air tawar yang dihasilkan dipompa ke tangki penyimpanan. Air garam pekat (brine) yang tersisa di evaporator dibuang ke laut."}]}]}, {"title": "5. Penukar Panas / Heat Exchanger", "paragraphs": ["Mentransfer panas dari satu fluida ke fluida lain tanpa mencampurnya. Di kamar mesin, ini biasanya digunakan untuk mendinginkan atau memanaskan fluida. / Transfers heat from one fluid to another without mixing them. In the engine room, this is usually used to cool or heat a fluid."], "sub_sections": [{"title": "A. Konsep Dasar Perpindahan Panas / Basic Concepts of Heat Transfer", "points": [{"main": "Pendinginan (Cooling): Proses yang paling umum, membuang panas dari fluida kerja. Contoh: mendinginkan oli pelumas."}, {"main": "Pemanasan (Heating): Menambahkan panas ke fluida untuk mengubah propertinya. Contoh: memanaskan bahan bakar berat (HFO) agar viskositasnya turun."}, {"main": "Penguapan (Evaporation): Mengubah cairan menjadi gas dengan menambahkan panas. Contoh: evaporator pada sistem AC atau generator air tawar."}, {"main": "Pengkondensasian (Condensation): Mengubah gas menjadi cairan dengan membuang panas. Contoh: kondensor pada sistem AC atau generator air tawar."}]}, {"title": "B. Jenis-jenis Penukar Panas / Types of Heat Exchangers", "paragraphs": ["Dua jenis yang paling umum di kapal adalah:"], "points": [{"main": "Jenis Pelat (Plate Type): Terdiri dari tumpukan pelat logam tipis bergelombang. Sangat efisien, kompak, dan mudah dibersihkan, tetapi rentan terhadap kebocoran paking (gasket)."}, {"main": "Jenis Shell & Tube: Terdiri dari sebuah bejana (shell) dengan sekelompok pipa (tube bundle) di dalamnya. Sangat kuat, andal untuk tekanan tinggi, tetapi lebih besar dan kurang efisien dibandingkan jenis pelat."}]}, {"title": "C. Alat/Mesin Penukar Panas di Kapal / Heat Exchanger Equipment on Ships", "points": [{"main": "Pendingin Oli Pelumas (Lube Oil Cooler): Mendinginkan oli pelumas panas dari mesin."}, {"main": "Pendingin Air Tawar Jaket (Jacket Water Cooler): Pendingin utama untuk sistem pendingin mesin, biasanya menggunakan air laut sebagai media pendingin."}, {"main": "Pemanas Bahan Bakar (Fuel Oil Heater): Memanaskan HFO menggunakan uap atau oli termal agar mudah dipompa dan disemprotkan."}, {"main": "Kondensor (Condenser): Mendinginkan uap (misalnya uap refrigerant di sistem AC) menjadi cairan."}, {"main": "Evaporator / Generator Air Tawar: Menggunakan panas untuk menguapkan air laut."}]}]}, {"title": "6. Sistem Refrigerasi & Pendingin Provisi / Refrigeration & Provision Cooling Systems", "paragraphs": ["Sistem ini penting untuk mengawetkan makanan bagi kru selama pelayaran. Sistem ini menggunakan siklus kompresi uap untuk memindahkan panas dari dalam ruang penyimpanan ke luar.", "This system is essential for preserving food for the crew during voyages. It uses a vapor compression cycle to move heat from inside the storage rooms to the outside."], "sub_sections": [{"title": "Ruang Chiller vs. Freezer", "points": [{"main": "Chiller (Ruang Pendingin): Untuk menyimpan sayuran, buah, dan produk susu. Suhu dijaga sekitar <strong>+2°C hingga +4°C</strong>."}, {"main": "Freezer (Ruang Pembeku): Untuk menyimpan daging dan ikan. Suhu dijaga sangat rendah, sekitar <strong>-18°C hingga -25°C</strong>."}]}, {"title": "Siklus Kompresi Uap / Vapor Compression Cycle", "points": [{"main": "Kompresor: Menghisap refrigerant gas bertekanan rendah dan memampatkannya menjadi gas bertekanan & bersuhu tinggi."}, {"main": "Kondensor: Gas panas dari kompresor didinginkan (biasanya oleh air laut) dan berubah menjadi cairan bertekanan tinggi."}, {"main": "Katup Ekspansi: Cairan bertekanan tinggi melewati katup ini, tekanannya turun drastis, membuatnya menjadi campuran cairan-gas yang sangat dingin."}, {"main": "Evaporator: Campuran dingin ini mengalir melalui pipa-pipa di dalam ruang Chiller/Freezer. Ia menyerap panas dari ruangan, menyebabkan cairan refrigerant mendidih menjadi gas dan mendinginkan ruangan. Gas ini kemudian kembali ke kompresor."}]}]}, {"title": "7. Sistem AC & Ventilasi / Air-Conditioning & Ventilation Systems", "paragraphs": ["Sistem ini memastikan lingkungan kerja dan hidup yang aman dan nyaman di atas kapal.", "These systems ensure a safe and comfortable working and living environment on board."], "points": [{"main": "Ventilasi (Ventilation): Tujuannya adalah pertukaran udara.", "sub_points": [{"main": "Kamar Mesin: Memasok udara segar untuk pembakaran mesin dan membuang udara panas untuk mendinginkan ruangan."}, {"main": "Akomodasi & Palka: Menghilangkan udara pengap, bau, dan gas berbahaya, serta memasok oksigen."}]}, {"main": "Pendingin Udara (Air-Conditioning - AC): Ini adalah sistem kontrol iklim total.", "sub_points": [{"main": "Fungsi: Tidak hanya mendinginkan, tetapi juga mengontrol <strong>kelembaban (dehumidify)</strong>, <strong>membersihkan (filter)</strong>, dan <strong>mensirkulasikan</strong> udara."}, {"main": "Aplikasi: Ruang akomodasi, anjungan, dan ruang kontrol mesin untuk kenyamanan kru dan melindungi peralatan elektronik."}, {"main": "Prinsip Kerja: Menggunakan siklus refrigerasi yang sama dengan pendingin provisi untuk mendinginkan udara."}]}]}, {"title": "8. Pembangkit Listrik (Generator) / Electrical Power Generation (Generators)", "paragraphs": ["Generator di kapal, sering disebut genset (generator set), menghasilkan semua tenaga listrik yang dibutuhkan untuk mengoperasikan seluruh mesin, sistem navigasi, pencahayaan, dan akomodasi.", "Shipboard generators, often called gensets, produce all the electrical power required to operate all machinery, navigation systems, lighting, and accommodation."], "sub_sections": [{"title": "Komponen Utama / Main Components", "points": [{"main": "Prime Mover: Penggerak utama, hampir selalu berupa mesin diesel 4-tak (four-stroke diesel engine) yang andal."}, {"main": "Alternator: Bagian yang mengubah energi mekanik (putaran) dari mesin diesel menjadi energi listrik AC (arus bolak-balik)."}]}, {"title": "Sinkronisasi (Paralleling Generators)", "paragraphs": ["Kapal memiliki beberapa genset. Menjalankannya secara paralel (bersama-sama) sangat penting untuk:", "Untuk menyinkronkan generator ke switchboard utama (busbar), <strong>empat kondisi</strong> harus terpenuhi secara tepat:"], "points": [{"main": "Pembagian Beban (Load Sharing): Ketika kebutuhan listrik melebihi kapasitas satu generator (misalnya saat manuver menggunakan bow thruster), generator lain dapat dijalankan secara paralel untuk berbagi beban."}, {"main": "Redundansi & Keamanan (Redundancy & Safety): Jika satu generator gagal, generator lain dapat mengambil alih beban, mencegah pemadaman total (blackout)."}, {"main": "Tegangan Sama (Same Voltage): Tegangan generator yang akan masuk harus sama dengan tegangan busbar."}, {"main": "Frekuensi Sama (Same Frequency): Frekuensi generator (diatur oleh kecepatan mesin) harus sama persis dengan frekuensi busbar."}, {"main": "Urutan Fasa Sama (Same Phase Sequence): Urutan fasa (misalnya, R-Y-B) harus cocok. Ini sudah diatur saat instalasi."}, {"main": "Sudut Fasa Sama (Same Phase Angle): Gelombang sinus dari tegangan generator harus sejajar (in-phase) dengan gelombang sinus busbar. Ini diperiksa menggunakan alat yang disebut synchroscope."}]}]}]}'),
('other-auxiliaries', 3, 'Fokus Tata Bahasa', 'Grammar Focus', '{"type": "grammar", "items": [{"title": "1. Gerunds as Subjects (Verb-ing)", "explanation": "Kita dapat menggunakan bentuk -ing dari kata kerja (gerund) sebagai subjek kalimat untuk menjelaskan suatu tindakan atau proses. / We can use the -ing form of a verb (a gerund) as the subject of a sentence to describe an action or process.", "examples": ["<strong>Pumping</strong> liquids is the main function of a pump.", "<strong>Separating</strong> water from fuel is the purpose of a purifier.", "<strong>Synchronizing</strong> generators requires careful attention."]}, {"title": "2. Clauses of Purpose (to + verb)", "explanation": "Kita menggunakan ''to + verb'' (infinitive) untuk menjelaskan tujuan dari suatu tindakan atau komponen. / We use ''to + verb'' (infinitive) to explain the purpose of an action or a component.", "examples": ["A compressor is used <strong>to provide</strong> high-pressure air.", "We use a heat exchanger <strong>to cool</strong> the lube oil.", "The engineer started another generator <strong>to share</strong> the load."]}]}'),
('other-auxiliaries', 4, 'Contoh Percakapan', 'Conversation Example', '{"type": "conversation", "scenario": "Skenario: Second Engineer dan Oiler sedang melakukan pengecekan rutin di kamar mesin.", "scenario_en": "Scenario: The Second Engineer and an Oiler are on a routine check in the engine room.", "lines": [{"speaker": "Second Engineer", "dialogue": "Oiler, let''s start our rounds. First, check the main sea water pump. What''s the discharge pressure and are there any leaks from the mechanical seal?", "dialogue_en": "Oiler, mari kita mulai keliling. Pertama, periksa pompa air laut utama. Berapa tekanan keluarnya dan apakah ada kebocoran dari perapat mekanisnya?"}, {"speaker": "Oiler", "dialogue": "The pump pressure is steady at 2.5 bar, sir. The seal is dry, no leaks detected. The motor temperature feels normal.", "dialogue_en": "Tekanan pompa stabil di 2,5 bar, Pak. Perapatnya kering, tidak ada kebocoran. Suhu motor terasa normal."}, {"speaker": "Second Engineer", "dialogue": "Good. Now, over to the purifiers. How is the fuel oil purifier running? Did it complete its last de-sludge cycle correctly?", "dialogue_en": "Bagus. Sekarang, kita ke pemurni. Bagaimana kondisi pemurni bahan bakar? Apakah siklus pembuangan lumpur terakhirnya berjalan dengan benar?"}, {"speaker": "Oiler", "dialogue": "Yes, sir. It de-sludged about 15 minutes ago. The operating water pressure was correct, and the bowl is running quietly. The flow rate is also stable.", "dialogue_en": "Ya, Pak. Proses de-sludge berjalan 15 menit yang lalu. Tekanan air operasi sudah benar, dan bowl-nya berputar dengan tenang. Laju alirannya juga stabil."}, {"speaker": "Second Engineer", "dialogue": "Excellent. Let''s check the Fresh Water Generator. What''s the vacuum reading and how much fresh water are we producing per hour?", "dialogue_en": "Bagus sekali. Mari kita periksa Generator Air Tawar. Berapa pembacaan vakumnya dan berapa banyak air tawar yang kita produksi per jam?"}, {"speaker": "Oiler", "dialogue": "The vacuum is holding strong at about 92% on the gauge. Production is good, around 1.2 tons per hour, sir.", "dialogue_en": "Vakumnya kuat, sekitar 92% pada alat ukur. Produksi bagus, sekitar 1,2 ton per jam, Pak."}, {"speaker": "Second Engineer", "dialogue": "Perfect. Now, let''s check the power situation. How''s number two generator running? What''s the load in kilowatts?", "dialogue_en": "Sempurna. Sekarang, mari kita periksa situasi kelistrikan. Bagaimana kondisi generator nomor dua? Berapa bebannya dalam kilowatt?"}, {"speaker": "Oiler", "dialogue": "Number two generator is running smoothly, sir. The load is about 250 kW. All parameters are normal.", "dialogue_en": "Generator nomor dua berjalan lancar, Pak. Bebannya sekitar 250 kW. Semua parameter normal."}, {"speaker": "Second Engineer", "dialogue": "Okay. The bridge just called, they will start cargo operations in about an hour. That means the cranes will be running. We''ll need more power. Go and prepare number one generator for start-up. We will need to synchronize it.", "dialogue_en": "Oke. Anjungan baru saja menelepon, mereka akan memulai operasi kargo sekitar satu jam lagi. Itu artinya crane akan beroperasi. Kita akan butuh daya lebih. Pergi dan siapkan generator nomor satu untuk dinyalakan. Kita perlu menyinkronkannya."}, {"speaker": "Oiler", "dialogue": "Understood, sir. I will perform the pre-start checks on number one generator and have it ready for synchronization.", "dialogue_en": "Dimengerti, Pak. Saya akan melakukan pemeriksaan pra-start pada generator nomor satu dan menyiapkannya untuk sinkronisasi."}]}');